/// <summary>
/// Checks the possibility of turning on the feature.
/// </summary>
public void clicked()
{
    DialogButton dialogButtonStatus = DialogButton::Ok;
    /// We should warn a user about additional actions when he turns on the feature.
    #EECountryRegionCodes
    if (this.value() == NoYes::No && !SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAll))
    {
        dialogButtonStatus = Box::okCancel(strFmt("@VatDueDate:EnableDateVATRegisterWarning", "@GLS109142","@VatDueDate:CheckConsistencyName", "@SYS10437"), DialogButton::Cancel);
    }
    if (dialogButtonStatus == DialogButton::Ok)
    {
        super();
    }
}

