public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(SourceDocumentTmpTypeInformation), this);

    select firstonly sourceDocumentTmpTypeInformation;
    sysTableLookup.addLookupfield(fieldNum(SourceDocumentTmpTypeInformation, EnumTranslatedName));
    sysTableLookup.setLabel("@SYS317515");
    sysTableLookup.parmTmpBuffer(sourceDocumentTmpTypeInformation);
    sysTableLookup.performFormLookup();
}

public boolean validate()
{
    SourceDocumentTmpTypeInformation    localSourceDocumentTmpTypeInformation;
    boolean                             isValid;
    str                                 selection;

    isValid = super();

    if (isValid)
    {
        selection = this.text();
        element.getSourceDocumentType(true, subledgerJournalTransferRule, selection);
        localSourceDocumentTmpTypeInformation = SourceDocumentType::getSourceDocumentTypeInformation(tableId2name(tableNum(SubledgerJournalTransferRule)));
        select firstonly RecId from localSourceDocumentTmpTypeInformation
            where localSourceDocumentTmpTypeInformation.ClassId == SubledgerJournalTransferRule.SourceDocumentType;

        if (!localSourceDocumentTmpTypeInformation)
        {
            SubledgerJournalTransferRule.SourceDocumentType = -1;
            isValid = checkFailed("@SYS344782");
        }
    }

    return isValid;
}

