public boolean modified()
{
    boolean ret = super();

    LedgerChartOfAccounts ledgerCOA = element.getLedgerChartOfAccounts();

    if (ledgerCOA)
    {
        LedgerChartOfAccounts_Description.text(ledgerCOA.Description);

        MainAccount::addSecureRangeForChartOfAccounts(MainAccount_ds.query().dataSourceName(tableStr(MainAccount)), ledgerCOA.RecId);
        LedgerTransSettlementAccount_ds.executeQuery();
        LedgerTransSettlementAccount_ds.research();
    }

    return ret;
}

public void lookup()
{
    super();

    LedgerChartOfAccounts chartOfAccountsLookup;
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(LedgerChartOfAccounts), this);
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(LedgerChartOfAccounts));
    QueryBuildRange queryBuildRangeName = queryBuildDataSource.addRange(fieldNum(LedgerChartOfAccounts, Name));

    // Create a lookup that has the Name and Description fields from the LedgerCOA table and is sorted on the name field by default
    queryBuildDataSource.addSortField(fieldNum(LedgerChartOfAccounts, Name));
    sysTableLookup.addLookupfield(fieldNum(LedgerChartOfAccounts, Name));
    sysTableLookup.addLookupfield(fieldNum(LedgerChartOfAccounts, Description));

    // Open the lookup with the built query
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public Common lookupReference()
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(MainAccount), this, true);

    // Do not call super as we're providing our own custom lookup logic.

    // Display the MainAccountID and Name fields in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(MainAccount, MainAccountId));
    sysTableLookup.addLookupMethod(tableMethodStr(MainAccount, localizedName));

    // Create a custom Query
    Query lookupQuery = new Query();
    QueryBuildDataSource qbds = lookupQuery.addDataSource(tableNum(MainAccount));
    MainAccount::addSecureRangeForChartOfAccounts(qbds, element.getLedgerChartOfAccounts().RecId);
    sysTableLookup.parmQuery(lookupQuery);

    return sysTableLookup.performFormLookup();
}

public Common resolveReference()
{
    // Do not call super as we're providing our own disambiguation logic.

    MainAccountNum mainAccountId = this.filterValue(AbsoluteFieldBinding::construct(fieldStr(MainAccount, MainAccountId), tableStr(MainAccount))).value();

    // Only search current COA
    MainAccount resolvedRecord;

    select firstonly resolvedRecord where
        resolvedRecord.MainAccountId == mainAccountId &&
        resolvedRecord.LedgerChartOfAccounts == element.getLedgerChartOfAccounts().RecId;

    return resolvedRecord;
}

public boolean modified()
{
    boolean ret = super();

    mainAccount_DS.cacheCalculateMethod(tableMethodStr(MainAccount, localizedName));

    element.enableOrDisableKeepDetails();

    DebCredProposal debitCreditProposal = MainAccount.DebitCreditProposal;
    element.defaultTypicalBalance(debitCreditProposal);

    return ret;
}
