public void dataOperationEnable()
{
    m_dataOperation.enabled(ledgerRRGOperationTable.DataType == LedgerRRGDataType_RU::Constant);
}

public void dataTypeModified()
{
    ledgerRRGOperationTable.AccountNumMask = EmptyStr;
    ledgerRRGOperationTable.OffsetAccountNumMask = EmptyStr;
    ledgerRRGOperationTable.BalanceType = LedgerRRGBalancePeriodType_RU::None;
    ledgerRRGOperationTable.TypeByCorrect = LedgerRRGTypeByCorrect_RU::None;

    switch (ledgerRRGOperationTable.DataType)
    {
        case LedgerRRGDataType_RU::Contractor :
            ledgerRRGOperationTable.ModelNum         = EmptyStr;
            ledgerRRGOperationTable.LedgerPeriodCode = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::ActiveBalance;
            break;

        case LedgerRRGDataType_RU::Trans    :
        case LedgerRRGDataType_RU::DimFocusBalance :
            ledgerRRGOperationTable.ModelNum         = EmptyStr;
            ledgerRRGOperationTable.LedgerPeriodCode = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::Balance;
            ledgerRRGOperationTable.AccountIntervalType       = LedgerRRGIntervalType_RU::None;
            ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::None;
            break;

        case LedgerRRGDataType_RU::Budget   :
            ledgerRRGOperationTable.LedgerPeriodCode = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::Balance;
            break;

        case LedgerRRGDataType_RU::Register :
            ledgerRRGOperationTable.ModelNum         = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::Turnover;
            break;

        case LedgerRRGDataType_RU::Constant :
            ledgerRRGOperationTable.ModelNum                  = EmptyStr;
            ledgerRRGOperationTable.LedgerPeriodCode          = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation           = LedgerRRGOperationType_RU::None;
            ledgerRRGOperationTable.AccountIntervalType       = LedgerRRGIntervalType_RU::None;
            ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::None;
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    this.dataOperationEnable();
    this.enableOperationControls();
    ledgerRRGOperationTable_ds.refresh();
}

public void enableAccountNumMask()
{
    FormDataObject objAccountNumMask, objAccountIntervalType;
    DimensionHierarchyId dimensionHierarchy;
    DimensionHierarchyLevel dimensionHierarchyLevel;
    DimensionAttribute dimensionAttribute;

    objAccountNumMask       = ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, AccountNumMask));
    objAccountIntervalType  = ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, AccountIntervalType));

    switch(ledgerRRGOperationTable.AccountIntervalType)
    {
        case LedgerRRGIntervalType_RU::Account  :

            objAccountNumMask.enabled(true);
            break;

        case LedgerRRGIntervalType_RU::Interval :
        case LedgerRRGIntervalType_RU::None     :

            objAccountNumMask.enabled(false);
            ledgerRRGOperationTable.AccountNumMask = EmptyStr;
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    if (ledgerRRGOperationTable.DataType == LedgerRRGDataType_RU::DimFocusBalance)
    {
        dimensionHierarchy = ledgerRRGOperationTable.getDimensionSet();

        select firstOnly RecId from dimensionAttribute
            where dimensionAttribute.Type == DimensionAttributeType::MainAccount
        exists join dimensionHierarchyLevel
            where dimensionHierarchyLevel.DimensionAttribute == dimensionAttribute.RecId
               && dimensionHierarchyLevel.DimensionHierarchy == dimensionHierarchy;

        objAccountIntervalType.enabled(objAccountIntervalType.enabled() && dimensionAttribute.RecId != 0);
        objAccountNumMask.enabled(objAccountNumMask.enabled() && dimensionAttribute.RecId != 0);
    }
}

public void enableBalanceType(boolean bEnable)
{
    ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, BalanceType)).enabled(bEnable);

    if (! bEnable)
    {
        ledgerRRGOperationTable.BalanceType = LedgerRRGBalancePeriodType_RU::None;
    }
}

public void enableCellControls()
{
    FormDataObject  objModelNum;

    void enableModelNum(boolean _bEnable)
    {
        objModelNum.enabled(_bEnable);

        if ( ! _bEnable)
        {
            ledgerRRGCellTable.ModelNum = EmptyStr;
        }
    }

    objModelNum = ledgerRRGCellTable_ds.object(fieldNum(LedgerRRGCellTable_RU, ModelNum));
    ledgerRRGEOperationGroupInquire_W.enabled(ledgerRRGCellTable.RecId);

    switch (ledgerRRGCellTable.DataType)
