            TransOperationsTaxPage_Cell.visible(true);
            operationTransTypePage_Cell.visible(ledgerRRGCellTable.DataType != LedgerRRGDataType_RU::DimFocusBalance);
            enableModelNum(false);
            break;

        case LedgerRRGDataType_RU::Budget :
