            operationTransTypePage_Cell.visible(false);
            enableModelNum(true);
            break;

        case LedgerRRGDataType_RU::Register :
            rTax25Register_Cells.visible(true);
            ReversingEntry.visible(false);
            TransOperationsTaxPage_Cell.visible(false);
            operationTransTypePage_Cell.visible(false);
            enableModelNum(false);
            break;

        case LedgerRRGDataType_RU::Contractor :
            rTax25Register_Cells.visible(false);
            ReversingEntry.visible(false);
            TransOperationsTaxPage_Cell.visible(false);
            operationTransTypePage_Cell.visible(false);
