public void init()
{
    super();

    if (this.args().dataset() != tableNum(LedgerRRGReportTable_RU) || ! this.args().record())
        throw error(strFmt("@SYS19096", element.name(), tableStr(LedgerRRGReportTable_RU)));

    m_ledgerRRGReportTable = this.args().record();


    this.setCellCodeLookupButton(m_ledgerRRGReportTable.ReportType == LedgerRRGReportType_RU::Word);

    element.enableCellControls();
    element.enableMenuLedgerRRGAccountInterval();

    element.updateOperationsLineNumFrame();
    element.updateButtonGroupChangePosition();
}

public void ledgerOperationModified()
{
    if( ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorr       ||
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorrCredit ||
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorrDebit)
    {
        ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::Account;
    }
    else
    {
        ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::None;
    }

    element.enableOperationControls();
}

public boolean operationIsBalance()
{
    return (ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::Balance       ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::BalanceCredit ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::BalanceDebit  ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::ActiveBalance ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::PassiveBalance
            );
}

public void refresh(LedgerRRGCellCode_RU _cellCode = '')
{
    LedgerRRGCellTable_RU cellTable;

    if (_cellCode)
    {
        ledgerRRGCellTable_ds.executeQuery();
        select cellTable where
            cellTable.ReportRecId   == m_ledgerRRGReportTable.RecId   &&
            cellTable.CellCode      == _cellCode;

        ledgerRRGCellTable_ds.positionToRecord(cellTable);
    }
    else
    {
        ledgerRRGCellTable_ds.reread();
        ledgerRRGCellTable_ds.refresh();
        ledgerRRGDimensionInterval_Cell_ds.executeQuery();
        ledgerRRGTransType_Cell_ds.executeQuery();
        tmpLedgerRRGTransOperationsTax_Cell_ds.executeQuery();
        ledgerRRGOperationTable_ds.executeQuery();
        ledgerRRGCellTable_ds.active();

    }
}

