public void updateButtonGroupChangePosition()
{
    boolean   allowEdit = !ledgerRRGCellTable.IsStatic;

    btUp.enabled(false);
    btDown.enabled(false);

    if (ledgerRRGOperationTable.LineNum > lineNumMin)
    {
        btUp.enabled(allowEdit);
    }

    if (ledgerRRGOperationTable.LineNum < lineNumMax)
    {
        btDown.enabled(allowEdit);
    }
}

