public void moveRecord(int _direction = MoveUp)
{
    LedgerRRGOperationTable_RU      localLedgerRRGOperationTable;
    LineNum                         lineNum;
    RecId                           recId;

    ledgerRRGOperationTable.write();

    ttsbegin;

    switch (_direction)
    {

        case MoveUp  :

            select firstonly forupdate localLedgerRRGOperationTable
                order by LineNum desc
                where   localLedgerRRGOperationTable.CellTableId == tableNum(LedgerRRGCellTable_RU) &&
                        localLedgerRRGOperationTable.CellRecId   == ledgerRRGCellTable.RecId        &&
                        localLedgerRRGOperationTable.LineNum     <  ledgerRRGOperationTable.LineNum;
            break;

        case MoveDown :

            select firstonly forupdate localLedgerRRGOperationTable
                order by LineNum asc
                where   localLedgerRRGOperationTable.CellTableId == tableNum(LedgerRRGCellTable_RU) &&
                        localLedgerRRGOperationTable.CellRecId   == ledgerRRGCellTable.RecId        &&
                        localLedgerRRGOperationTable.LineNum     >  ledgerRRGOperationTable.LineNum;

            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    if (localLedgerRRGOperationTable)
    {
        lineNum = localLedgerRRGOperationTable.LineNum;

        localLedgerRRGOperationTable.LineNum = ledgerRRGOperationTable.LineNum;
        localLedgerRRGOperationTable.update();

        ledgerRRGOperationTable.LineNum = lineNum;
        ledgerRRGOperationTable.write();

        ttscommit;

        recId =  ledgerRRGOperationTable.RecId;

        this.removeFilter();
        this.refresh();
        this.positionToRecord(LedgerRRGOperationTable_RU::findByRecID(recId));
    }
    else
    {
        ttsabort;
    }
}

