[Form]
public class LedgerRRGEFunctionSetup_W extends FormRun
{
    LedgerRRGEQueries_W             eQuery;
    LedgerRRGELineNumSupport_W      lineNumSupport;
    LedgerRRGDataType_RU            lastDataType;

    private const int               MoveUp	        = 0;
    private const int               MoveDown	    = 1;
    private const str               EmptyStr	    = '';

}

public void dataTypeModified()
{
    ledgerRRGOperationTable.AccountNumMask          = EmptyStr;
    ledgerRRGOperationTable.OffsetAccountNumMask    = EmptyStr;
    ledgerRRGOperationTable.BalanceType             = LedgerRRGBalancePeriodType_RU::None;
    ledgerRRGOperationTable.TypeByCorrect           = LedgerRRGTypeByCorrect_RU::None;

    switch (ledgerRRGOperationTable.DataType)
    {
        case LedgerRRGDataType_RU::Contractor :
            ledgerRRGOperationTable.ModelNum         = EmptyStr;
            ledgerRRGOperationTable.LedgerPeriodCode = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::ActiveBalance;
            break;

        case LedgerRRGDataType_RU::Trans    :
        case LedgerRRGDataType_RU::DimFocusBalance :
            ledgerRRGOperationTable.ModelNum         = EmptyStr;
            ledgerRRGOperationTable.LedgerPeriodCode = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::Balance;
            break;

        case LedgerRRGDataType_RU::Budget   :
            ledgerRRGOperationTable.LedgerPeriodCode = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::Balance;
            break;

        case LedgerRRGDataType_RU::Register :
            ledgerRRGOperationTable.ModelNum         = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation  = LedgerRRGOperationType_RU::Turnover;
            break;

        case LedgerRRGDataType_RU::Constant :
            ledgerRRGOperationTable.ModelNum                  = EmptyStr;
            ledgerRRGOperationTable.LedgerPeriodCode          = EmptyStr;
            ledgerRRGOperationTable.LedgerOperation           = LedgerRRGOperationType_RU::None;
            ledgerRRGOperationTable.AccountIntervalType       = LedgerRRGIntervalType_RU::None;
            ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::None;
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    this.enableOperationControls();
    ledgerRRGOperationTable_ds.refresh();
}

public void enableAccountNumMask()
{
    FormDataObject objAccountNumMask, objAccountIntervalType;
    DimensionHierarchyId dimensionHierarchy;
    DimensionHierarchyLevel dimensionHierarchyLevel;
    DimensionAttribute dimensionAttribute;

    objAccountNumMask       = ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, AccountNumMask));
    objAccountIntervalType  = ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, AccountIntervalType));

    objAccountNumMask.enabled(ledgerRRGOperationTable.AccountIntervalType == LedgerRRGIntervalType_RU::Account);

    if (ledgerRRGOperationTable.DataType == LedgerRRGDataType_RU::DimFocusBalance)
    {
        dimensionHierarchy = ledgerRRGOperationTable.getDimensionSet();

        select firstOnly RecId from dimensionAttribute
            where dimensionAttribute.Type == DimensionAttributeType::MainAccount
        exists join dimensionHierarchyLevel
            where dimensionHierarchyLevel.DimensionAttribute == dimensionAttribute.RecId
               && dimensionHierarchyLevel.DimensionHierarchy == dimensionHierarchy;

        objAccountIntervalType.enabled(objAccountIntervalType.enabled() && dimensionAttribute.RecId != 0);
        objAccountNumMask.enabled(objAccountNumMask.enabled() && dimensionAttribute.RecId != 0);
    }
}

public void enableBalanceType()
{
    ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, BalanceType)).enabled(element.operationIsBalance());
}

public void enableCorrAccount()
{
    ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, OffsetAccountIntervalType)).enabled(
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorr       ||
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorrCredit ||
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorrDebit);

    element.enableOffsetAccountNumMask();
}

public void enableMenuLedgerRRGAccountInterval()
{
    ledgerRRGAccountInterval.enabled(ledgerRRGOperationTable.AccountIntervalType        == LedgerRRGIntervalType_RU::Interval ||
                                     ledgerRRGOperationTable.OffsetAccountIntervalType  == LedgerRRGIntervalType_RU::Interval);
}

public void enableOffsetAccountNumMask()
{
    ledgerRRGOperationTable_ds.object(fieldNum(LedgerRRGOperationTable_RU, OffsetAccountNumMask)).enabled(
        ledgerRRGOperationTable.OffsetAccountIntervalType == LedgerRRGIntervalType_RU::Account);
}

public void enableOperationControls(LedgerRRGOperationTable_RU _ledgerRRGOperationTable = ledgerRRGOperationTable)
{
    FormControl selectedControl;

    selectedControl = element.selectedControl();

    if (lastDataType != _ledgerRRGOperationTable.DataType || !ledgerRRGOperationTable)
    {
        lastDataType = _ledgerRRGOperationTable.DataType;

        generalTransactionPage.visible(false);
        reversingGroup.visible(false);
        budgetPage.visible(false);
        constantPage.visible(false);
        queryPage.visible(false);
        contractorPage.visible(false);
        requisitePage.visible(false);
        rTax25RegisterPage.visible(false);
        tabFinancialDimensions.viewEditMode(false);
        operationTransTypePage.visible(false);
        transOperationsTaxPage.visible(false);

        switch (_ledgerRRGOperationTable.DataType)
        {
            case LedgerRRGDataType_RU::Trans:
                generalTransactionPage.visible(true);
                reversingGroup.visible(true);
                operationTransTypePage.visible(true);
                transOperationsTaxPage.visible(true);
                tabFinancialDimensions.visible(true);
                generalTransactionPage.setFocus();
                break;

            case LedgerRRGDataType_RU::DimFocusBalance:
                generalTransactionPage.visible(true);
                transOperationsTaxPage.visible(true);
                tabFinancialDimensions.visible(true);
                generalTransactionPage.setFocus();
                break;

            case LedgerRRGDataType_RU::Budget:
                budgetPage.visible(true);
                tabFinancialDimensions.visible(true);
                budgetPage.setFocus();
                break;

            case LedgerRRGDataType_RU::Register:
                rTax25RegisterPage.visible(true);
                tabFinancialDimensions.visible(true);
                rTax25RegisterPage.setFocus();
                break;

            case LedgerRRGDataType_RU::Constant :
                constantPage.visible(true);
                constantPage.setFocus();
                break;

            case LedgerRRGDataType_RU::Contractor :
                contractorPage.visible(true);
                contractorPage.setFocus();
                break;

            default :
                throw error(Error::wrongUseOfFunction(funcName()));
        }
    }

    element.enableCorrAccount();
    element.enableAccountNumMask();
    element.enableOffsetAccountNumMask();
    element.enableBalanceType();
    element.enableMenuLedgerRRGAccountInterval();

    if (selectedControl)
    {
        selectedControl.setFocus();
    }
}

public void init()
{
    LedgerRRGOperationTable_RU localOperationTable;

    if (!this.args() || this.args().dataset() != tableNum(LedgerRRGEQueries_W) || !this.args().record())
    {
        throw error(strFmt("@SYS19096", element.name(), tableStr(LedgerRRGEQueries_W)));
    }

    super();

    eQuery = this.args().record();

    lineNumSupport = LedgerRRGELineNumSupport_W::newFromFormRun(element, ledgerRRGOperationTable_ds);

    select firstonly localOperationTable order by LineNum
        where localOperationTable.CellTableId   == eQuery.TableId &&
              localOperationTable.CellRecId     == eQuery.RecId;

    element.enableOperationControls(localOperationTable);
}

public void ledgerOperationModified()
{
    if( ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorr       ||
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorrCredit ||
        ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::TurnoverInCorrDebit)
    {
        ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::Account;
    }
    else
    {
        ledgerRRGOperationTable.OffsetAccountIntervalType = LedgerRRGIntervalType_RU::None;
    }

    if (!element.operationIsBalance())
    {
        ledgerRRGOperationTable.BalanceType = LedgerRRGBalancePeriodType_RU::None;
    }

    element.enableBalanceType();
    element.enableCorrAccount();
    ledgerRRGOperationTable_ds.refresh();
}

public boolean operationIsBalance()
{
    return (ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::Balance       ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::BalanceCredit ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::BalanceDebit  ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::ActiveBalance ||
            ledgerRRGOperationTable.LedgerOperation == LedgerRRGOperationType_RU::PassiveBalance
            );
}

