public void editQuery()
{
    SysQueryForm    sysQueryForm;
    SysQueryRun     sysQueryRun;
    FormRun         formRun;
    Args            args;



    if (ledgerRRGOperationTable.Query == conNull())
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }
    else
    {
        sysQueryRun  = ledgerRRGOperationTable.getSysQueryRun();
        sysQueryRun.promptLoadLastUsedQuery(false);
        sysQueryRun.promptAllowSave(false);
        sysQueryRun.promptShowSorting(false);
        sysQueryRun.promptSaveQueryPrUser(false);

        sysQueryForm = new SysQueryForm(sysQueryRun);

        args         = new Args(formStr(SysQueryForm));
        args.caller(sysQueryRun);

        formRun      = ClassFactory::formRunClassOnClient(args);
        formRun.init();
        formRun.run();
        formRun.wait();

        if (formRun.closedOk())
        {
            ledgerRRGOperationTable.initQuery(sysQueryRun);
        }
    }
}

