public void modified()
{
    super();

    if (ledgerRRGOperationTable.AccountIntervalType != LedgerRRGIntervalType_RU::Account)
    {
        ledgerRRGOperationTable.AccountNumMask = EmptyStr;
    }

    element.enableMenuLedgerRRGAccountInterval();
    element.enableAccountNumMask();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    switch (ledgerRRGOperationTable.DataType)
    {
        case LedgerRRGDataType_RU::Trans,
             LedgerRRGDataType_RU::Budget,
             LedgerRRGDataType_RU::Contractor,
             LedgerRRGDataType_RU::DimFocusBalance:
            MainAccount::lookupMainAccount(_formControl);
            break;

        case LedgerRRGDataType_RU::Register:
            RTax25ProfitTable::lookupLeafProfitId(_formControl);
            break;

        default :
            throw error(Error::wrongUseOfFunction(funcName()));
    }
}

