[Form]
public class LedgerTransHistoryVoucher extends FormRun implements LedgerTransHistoryVoucherIListPage
{
    private LedgerTransHistoryVoucherFormContext context;
    private LedgerTransHistoryVoucherFormQueryManager queryManager;
    private LedgerTransHistoryVoucherFormDisplayManager displayManager;
    private LedgerTransHistoryVoucherFormControlManager controlManager;

    #ISOCountryRegionCodes

}

[Hookable(false)]
public void init()
{
    context = LedgerTransHistoryVoucherFormContext::constructDefaultContext();
    controlManager = LedgerTransHistoryVoucherFormControlManager::newForLedgerTransHistoryVoucherListPage(this);
    queryManager = LedgerTransHistoryVoucherFormQueryManager::newForLedgerTransHistoryVoucherListPage(this);
    displayManager = new LedgerTransHistoryVoucherFormDisplayManager();

    super();

    controlManager.initalizeControls();

    this.promptUser();
}

private void promptUser()
{
    if (this.args().caller() is LedgerArchiveTransIOpenHistoryVoucherForArchiveTrans)
    {
        var archiveTransCaller = this.args().caller() as LedgerArchiveTransIOpenHistoryVoucherForArchiveTrans;
        var archiveTrans = archiveTransCaller.getArchiveTrans();

        queryManager.filterForGivenArchiveTrans(this, archiveTrans);
    }
    else
    {
        SysQueryRun queryRun = queryManager.buildSysQueryRun();

        if (!queryRun.prompt())
        {
            element.close();
        }
        else
        {
            if (context.getLedgerChineseVoucherEnabled_CN() &&
            SysQuery::findOrCreateRange(SysQuery::findOrCreateDataSource(queryRun.query(), tableNum(GeneralJournalEntryHistory)), fieldNum(GeneralJournalEntryHistory, AccountingDate)).value() == '')
            {
                warning("@SYS24455");
                element.close();
            }

            generalJournalEntryHistory_ds.query(queryRun.query());
        }
    }
}

public void executeQuery()
{
    if (!(element.args().caller() is LedgerArchiveTransIOpenHistoryVoucherForArchiveTrans))
    {
        QueryBuildDataSource dataSourceGeneralJournalEntryHistory;
        dataSourceGeneralJournalEntryHistory = this.query().dataSourceTable(tableNum(GeneralJournalEntryHistory));
        Ledger::addSecureRangeForLedger(dataSourceGeneralJournalEntryHistory, fieldNum(GeneralJournalEntryHistory, Ledger), Ledger::current());
    }

    super();
}

public void init()
{
    if (context.getLedgerChineseVoucherEnabled_CN())
    {
        // has to be done before super to ensure it is properly initialized
        this.linkType(FormLinkType::InnerJoin);
    }
    // isLegalEntityInCountryRegion call should still work even though it is in GeneralJournalEntry_W
    else if (GeneralJournalEntry_W::isLegalEntityInCountryRegion())
    {
        // has to be done before super to ensure it is properly initialized
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}
