Method.Source:
    [Form]
    public class LedgerTransPreview_RU extends FormRun
    {
        LedgerJournalTable          ledgerJournalTable;

        QueryBuildRange             debitCreditCriteria;
        LedgerBondShowTransMode_RU  showDebitCredit;

        TmpLedgerTransPreview_RU    ledgerTransPreview;
}

public void init()
{
    LedgerJournalCheckPost  ledgerJournalCheckPost;
    Common                  common = element.args().record();

    if (common.TableId == tablenum(LedgerJournalTable))
    {
        ledgerJournalTable = common;
    }
    else
    {
        throw error("@GLS104595");
    }
    if (! ledgerJournalTable.numOfVouchers())
    {
        throw error("@SYS27671");
    }

    if (LedgerParameters::find().Correspondence_RU)
    {
        showDebitCredit = LedgerBondShowTransMode_RU::ShowDebitOnly;
    }
    ttsbegin;
    ledgerJournalCheckPost   = LedgerJournalCheckPost::newLedgerJournalTable(ledgerJournalTable, NoYes::Yes);
    ledgerJournalCheckPost.parmPreview_RU(true);
    LedgerJournalCheckPost::processOperation(ledgerJournalCheckPost);

    super();

    ttsabort;
    Dictionary::dataFlush(tablenum(LedgerJournalTable));

    comboShowDebitCredit.selection(showDebitCredit);

    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, VATDueDate_W)).visible(TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled());
}

public void jumpRefMainAccount(DimensionDisplayValue _dimensionDisplayValue)
{
    DimensionAttributeLevelValueView dimAttibuteLevelValueView;

    MenuFunction   menuFunction  = new MenuFunction(menuitemDisplayStr(MainAccountEdit), MenuItemType::Display);

    MainAccountNum mainAccountId = conPeek(str2con_RU(_dimensionDisplayValue, DimensionParameters::getDimensionSegmentDelimiter(), true), 1);
    Args           args          = new Args();
    ;
    select firstonly EntityInstance from dimAttibuteLevelValueView
        where dimAttibuteLevelValueView.DisplayValue       == mainAccountId &&
              dimAttibuteLevelValueView.DimensionAttribute == DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount);

    args.record(MainAccount::find(dimAttibuteLevelValueView.EntityInstance));

    menuFunction.run(args);
}

public void init()
{
    QueryBuildDataSource        queryVouchers;

    vouchers.setTmp();

    TmpLedgerTransPreview_RU::fillForJournalNum(ledgerJournalTable.JournalNum, ledgerTransPreview, vouchers);

    super();

    queryVouchers = this.query().dataSourceNo(1);
    queryVouchers.clearDynalinks();
    queryVouchers.addSortField(fieldnum(GeneralJournalEntry, AccountingDate));
}

public void executeQuery()
{
    if (LedgerParameters::find().Correspondence_RU)
    {
        switch (showDebitCredit)
        {
            case LedgerBondShowTransMode_RU::ShowDebitOnly :
                debitCreditCriteria.value(enum2str(NoYes::No));
                break;

            case LedgerBondShowTransMode_RU::ShowCreditOnly :
                debitCreditCriteria.value(enum2str(NoYes::Yes));
                break;

            default :
                debitCreditCriteria.value(SysQuery::valueUnlimited());
        }
    }
    super();
}

public void init()
{
    QueryBuildDataSource qbds;

    ledgerTrans.setTmpData(ledgerTransPreview);

    super();

    qbds = this.query().dataSourceNo(1);
    qbds.clearDynalinks();

    qbds.addDynalink(fieldNum(TmpLedgerTransPreview_RU, Voucher), vouchers,   fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher));
    qbds.addDynalink(fieldNum(TmpLedgerTransPreview_RU, TransDate), vouchers, fieldNum(SubledgerVoucherGeneralJournalEntry, AccountingDate));
    qbds.clearRanges();
    debitCreditCriteria = qbds.addRange(fieldNum(TmpLedgerTransPreview_RU, IsCredit));
}

public void init()
{
    QueryBuildDataSource ds;
    super();
    ds = this.query().dataSourceTable(tableNum(LedgerJournalTrans));
    ds.clearDynalinks();

    ds.addDynalink(fieldNum(LedgerJournalTrans, Voucher), vouchers, fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher));
}

public boolean validateWrite()
{
    #IsoCountryRegionCodes
    boolean ret = true;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#IsoHU, #IsoPL]))
    {
        if (!ledgerJournalTransRCash.RefRecId)
        {
            ledgerJournalTrans_DS.write();
            ledgerJournalTransRCash.RefRecId = ledgerJournalTrans.RecId;
        }

        ret = super();
    }

    return ret;
}

void clicked()
{
    Args    args = new Args();

    args.record(ledgerTransPreview);
    args.caller(element);
    MenuFunction::runCalled(menuitemOutputStr(LedgerJournalPreview), MenuItemType::Output, false, args);
}

