    args.record(ledgerTransPreview);
    args.caller(element);
    args.parm(vouchers.Voucher);

    MenuFunction::runCalled(menuitemOutputStr(LedgerJournalVoucherPreview), MenuItemType::Output, false, args);
}

void clicked()
{
    ;
    ledgerJournalTransOffsetDimension.enabled(ledgerJournalTrans.RecId &&
                                              ledgerJournalTrans.OffsetAccountType != LedgerJournalACType::Ledger);

    ledgerJournalTransDimension.enabled(ledgerJournalTrans.RecId &&
                                        ledgerJournalTrans.AccountType != LedgerJournalACType::Ledger);

    super();
}

public boolean modified()
{
    boolean ret = super();

    showDebitCredit = this.selection();
    ledgerTrans_DS.executeQuery();

    return ret;
}
