private void addDynaLink(Common _common, FieldId _voucherId, FieldId _accountingDateId)
{
    QueryBuildDataSource ds;

    ds = element.getSubledgerVoucherLinkDataSource();
    ds.addDynalink(
        fieldNum(SubledgerVoucherGeneralJournalEntrySim_IT, Voucher),
        _common,
        _voucherId);

    if (_accountingDateId)
    {
        ds.addDynalink(
            fieldNum(SubledgerVoucherGeneralJournalEntrySim_IT, AccountingDate),
            _common,
            _accountingDateId);
    }
}

private void addDynaLinkForCreatedTransactionId(Query _query, Common _recordContainingCreatedTransactionId)
{
    QueryBuildDataSource ds;

    int fieldId = fieldName2Id(_recordContainingCreatedTransactionId.TableId, fieldStr(Common, CreatedTransactionId));

    if (!fieldId)
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    Debug::assert(_recordContainingCreatedTransactionId.CreatedTransactionId != 0);

    ds = _query.dataSourceTable(tableNum(GeneralJournalEntrySim_IT));
    Debug::assert(ds != null);

    ds.addDynalink(
        fieldNum(GeneralJournalEntrySim_IT, CreatedTransactionId),
        _recordContainingCreatedTransactionId,
        fieldId);
}

private void addDynaLinkVoucher(Common _common, FieldId _voucherId)
{
    QueryBuildDataSource ds;

    ds = element.getSubledgerVoucherLinkDataSource();
    ds.addDynalink(
        fieldNum(SubledgerVoucherGeneralJournalEntrySim_IT, Voucher),
        _common,
        _voucherId);
}

private FieldName getAccountingDateFieldName()
{
    return fieldStr(SubledgerVoucherGeneralJournalEntrySim_IT, AccountingDate);
}

/// <summary>
/// Gets a query range expression for the accounting date.
/// </summary>
/// <param name="_accountingDate">
/// The accounting date.
/// </param>
/// <returns>
/// A query range expression for the accounting date.
/// </returns>
private str getAccountingDateRangeExpression(TransDate _accountingDate)
{
    return strFmt(
        '(%1 == [%2])',
        this.getAccountingDateFieldName(),
        queryValue(_accountingDate));
}

private QueryBuildDataSource getSubledgerVoucherLinkDataSource(Query _query = GeneralJournalEntrySim_IT_ds.query())
{
    QueryBuildDataSource ds;

    ds = GeneralJournalEntrySim_IT_ds.query().dataSourceTable(tableNum(GeneralJournalEntrySim_IT));
    ds = ds.addDataSource(tableNum(SubledgerVoucherGeneralJournalEntrySim_IT));
    ds.addLink(fieldNum(GeneralJournalEntrySim_IT, RecId), fieldNum(SubledgerVoucherGeneralJournalEntrySim_IT, GeneralJournalEntry));
    SysQuery::findOrCreateRange(ds,fieldNum(SubledgerVoucherGeneralJournalEntrySim_IT, Voucher));
    SysQuery::findOrCreateRange(ds,fieldNum(SubledgerVoucherGeneralJournalEntrySim_IT, AccountingDate));

    return ds;
}

/// <summary>
/// Gets a range expression containing voucher and accounting date.
/// </summary>
/// <param name="_subledgerVoucher">
/// The voucher number.
/// </param>
/// <param name="_accountingDate">
/// The accounting date.
/// </param>
/// <returns>
/// A range expression containing voucher and accounting date.
/// </returns>
private str getVoucherDateRange(Voucher _subledgerVoucher, TransDate _accountingDate)
{
    return strFmt(
        '(%1 && %2)',
        this.getVoucherRangeExpression(_subledgerVoucher),
        this.getAccountingDateRangeExpression(_accountingDate));
}

private FieldName getVoucherFieldName()
{
    return fieldStr(SubledgerVoucherGeneralJournalEntrySim_IT, Voucher);
}

/// <summary>
/// Gets a query range expression for the voucher number.
/// </summary>
/// <param name="_voucher">
/// The voucher number.
/// </param>
/// <returns>
/// A query range expression for the voucher number.
/// </returns>
private str getVoucherRangeExpression(Voucher _voucher)
{
    return strFmt(
        '(%1 == ' + '\"%2\")',
        this.getVoucherFieldName(),
        any2str(_voucher));
}

public void init()
{
    Query query;
    QueryBuildDataSource ds;
    QueryBuildDataSource dsSubledgerVoucher;
    boolean isFromMenu;
    TableId sourceTable;
    Common common;
    VendTrans localVendTrans;
    VendInvoiceJour localVendInvoiceJour;

    // All is used to trigger a voucher only link and Chosen is used for voucher + date.
    AllChosen allChosen;

    #ISOCountryRegionCodes

    LedgerJournalTrans ledgerJournalTrans;
    QueryBuildRange range;
    Voucher taxSetOffVoucher;

    super();

    // Set the LedgerDimension reference on controls displaying LedgerAccount in order for the preview to work off a string field
    ledgerTrans_AccountNum.userDataItem(fieldNum(GeneralJournalAccountEntrySim_IT, LedgerDimension));

    overviewGrid.setFocus();


    // Show/hide these controls so users are not confused by the row-by-row enabling/disabling
    // of the client because of the global table. Row-by-row is not necessary because we
    // restrict to the current company via the Ledger.

    ledgerTrans_LedgerPostingJournalId.visible(false);
    identification_LedgerPostingJournalId.visible(false);

    if (element.args().lookupRecord())
    {
        // When called from report drill throughs the record is set in lookupRecord() and not
        // in record(), so get the value from lookuprecord() and set it in record().
        element.args().record(element.args().lookupRecord());
    }

    // Since the args.lookupRecord is not needed past this point, we have to clear
    // the record so that the subledgervoucherlink record is not looked up again when the request
    // comes from the ledger transfer voucher dialog.
    element.args().lookupRecord(null);

    // When there is no caller and no record passed in the args then isFromMenu will be true.
    isFromMenu = ((element.args().caller() == null) && (element.args().record() == null)) ||
        element.args().menuItemName() == menuItemDisplayStr(LedgerTransVoucherShowSysQuery);

    query = GeneralJournalEntrySim_IT_ds.query();

    ds = query.dataSourceTable(tableNum(MainAccount));

    // set the DimensionAttributeValueCombination-->MainAccount relation because the wrong one is the default
    ds.clearLinks();
    ds.addLink(fieldNum(DimensionAttributeValueCombination, MainAccount), fieldNum(MainAccount, RecId));

    // manually add the natural key query field
    ds.addRange(fieldNum(MainAccount, MainAccountId));

    // sort by accounting date because it is the preferred user sort
    // additionally sort by journal and voucher to provide a consistent order within duplicate dates
    ds = query.dataSourceTable(tableNum(GeneralJournalEntrySim_IT));
    ds.addSortField(fieldNum(GeneralJournalEntrySim_IT, AccountingDate));
    ds.addSortField(fieldNum(GeneralJournalEntrySim_IT, JournalNumber));
    ds.addSortField(fieldNum(GeneralJournalEntrySim_IT, SubledgerVoucher));

    if (isFromMenu)
    {
        element.updateQueryForMenu(query);
    }
    else
    {
        sourceTable = element.args().dataset();

        switch (sourceTable)
        {

            case tableNum(GeneralJournalEntrySim_IT):
                element.updateQueryForGeneralJournalEntry(query);
                break;


            case tableNum(LedgerJournalTrans):
                common = element.args().record();
                allChosen = element.args().parmEnum();

                if (allChosen == AllChosen::All)
                {
                    this.addDynaLinkVoucher(common, fieldNum(LedgerJournalTrans, Voucher));
                }
                else
                {
                    this.addDynaLink(common, fieldNum(LedgerJournalTrans, Voucher), fieldNum(LedgerJournalTrans, TransDate));
                }
                break;

            default:
                break;
        }
    }
}

private str formatTransDate(TransDate _date)
{
    return date2str(_date, -1, -1, -1, -1, -1, -1, DateFlags::FormatAll);
}

public void mcrExecuteRebatePayments(Query _query)
{
    QueryBuildDataSource queryBuildDataSource, queryBuildDataSourceCustTrans;

    CustTrans custTrans;
    VendTrans vendTrans;

    queryBuildDataSource = _query.dataSourceTable(tableNum(GeneralJournalEntrySim_IT));
    queryBuildDataSource.clearDynalinks();

    switch (element.args().record().TableId)
    {
        case tableNum(custTrans):
            custTrans = element.args().record();

            queryBuildDataSourceCustTrans = queryBuildDataSource.addDataSource(tableNum(CustTrans));
            queryBuildDataSourceCustTrans.addRange(fieldNum(CustTrans, Voucher)).value(custTrans.Voucher);
            queryBuildDataSourceCustTrans.addRange(fieldNum(CustTrans, transDate)).value(this.formatTransDate(custTrans.TransDate));

            queryBuildDataSourceCustTrans.joinMode(JoinMode::InnerJoin);

            queryBuildDataSourceCustTrans.addLink(fieldNum(GeneralJournalEntrySim_IT, JournalNumber), fieldNum(CustTrans, Voucher));
            queryBuildDataSourceCustTrans.addLink(fieldNum(GeneralJournalEntrySim_IT, AccountingDate), fieldNum(CustTrans, TransDate));
            break;

        case tableNum(vendTrans):
            vendTrans = element.args().record();

            queryBuildDataSourceCustTrans = queryBuildDataSource.addDataSource(tableNum(VendTrans));
            queryBuildDataSourceCustTrans.addRange(fieldNum(VendTrans, Voucher)).value(vendTrans.Voucher);
            queryBuildDataSourceCustTrans.addRange(fieldNum(VendTrans, transDate)).value(this.formatTransDate(vendTrans.TransDate));

            queryBuildDataSourceCustTrans.joinMode(JoinMode::InnerJoin);

            queryBuildDataSourceCustTrans.addLink(fieldNum(GeneralJournalEntrySim_IT, JournalNumber), fieldNum(VendTrans, Voucher));
            queryBuildDataSourceCustTrans.addLink(fieldNum(GeneralJournalEntrySim_IT, AccountingDate), fieldNum(VendTrans, TransDate));
            break;
    }
}

private void promptUser(Query _query)
{
    QueryBuildDataSource ds;
    QueryBuildRange qbr;
    QueryBuildDataSource dsVoucherType;

    // manually add the natural key query fields
    ds = _query.dataSourceTable(tableNum(GeneralJournalEntrySim_IT));
    ds.addRange(fieldNum(GeneralJournalEntrySim_IT, JournalNumber));
    ds.addRange(fieldNum(GeneralJournalEntrySim_IT, SubledgerVoucher));
    ds.addRange(fieldNum(GeneralJournalEntrySim_IT, AccountingDate));

    queryRun = new QueryRun(_query);

    if (!queryRun.prompt())
    {
        element.close();
    }

    GeneralJournalEntrySim_IT_ds.query(queryRun.query());
}

private void updateQueryForGeneralJournalEntry(Query _query)
{
    GeneralJournalEntrySim_IT inputGeneralJournalEntry;
    AllChosen allChosen;
    QueryBuildDataSource ds;
    QueryBuildDataSource qbd1, qbd2;
    QueryBuildRange qbrVoucher, qbrTransDate;
    SubledgerVoucherGeneralJournalEntrySim_IT subledgerVoucherLink;

    allChosen = element.args().parmEnum();

    inputGeneralJournalEntry = element.args().record() as GeneralJournalEntrySim_IT;

    ds = _query.dataSourceTable(tableNum(GeneralJournalEntrySim_IT));

    if (inputGeneralJournalEntry)
    {
        ds.addDynalink(
            fieldNum(GeneralJournalEntrySim_IT, SubledgerVoucher),
            inputGeneralJournalEntry,
            fieldNum(GeneralJournalEntrySim_IT, SubledgerVoucher));

        ds.addDynalink(
            fieldNum(GeneralJournalEntrySim_IT, AccountingDate),
            inputGeneralJournalEntry,
            fieldNum(GeneralJournalEntrySim_IT, AccountingDate));
    }
    else
    {
        ds.addDynalink(
        fieldNum(GeneralJournalEntrySim_IT, RecId),
        inputGeneralJournalEntry,
        fieldNum(GeneralJournalEntrySim_IT, RecId));
    }
}

private void updateQueryForMenu(Query _query)
{
    QueryBuildDataSource ds;
    TableId sourceTable;

    if (element.args().parm() == formStr(VendTrans) && isConfigurationkeyEnabled(configurationKeyNum(LedgerBasicWithholdingTax)))
    {
        ds = _query.dataSourceTable(tableNum(GeneralJournalAccountEntrySim_IT));

        ds.addRange(fieldNum(GeneralJournalAccountEntrySim_IT, Text));
    }

    // verify a GeneralJournalEntry buffer is not passed in the
    // menu case because if the assert fails, we should add a
    // ReferenceNumber range instead of prompting the user
    sourceTable = element.args().dataset();
    Debug::assert(sourceTable != tableNum(GeneralJournalEntrySim_IT));

    element.promptUser(_query);
}

