public void executeQuery()
{
    QueryBuildDataSource dataSourceGeneralJournalEntry;
    QueryBuildRange rangeLedger;

    dataSourceGeneralJournalEntry = this.query().dataSourceTable(tableNum(GeneralJournalEntrySim_IT));

    GeneralJournalEntrySim_IT::addSecureRangeForLedger(dataSourceGeneralJournalEntry);

    this.applyReportDrilldownRange();

    super();

}

public void init()
{
    super();

    GeneralJournalEntrySim_IT_ds.cacheAddMethod(tableMethodStr(GeneralJournalEntrySim_IT, displayIsFiscalYearClosed));
}

/// <summary>
/// Applies ranges for filtering records.
/// </summary>
/// <remarks>
/// Report drill downs were taking a while to load the form because to load the form executeQuery is ran
/// twice. The first executeQuery retrieves all viewable records and then the second executeQuery filters
/// down to the drill down value. The double executeQuery is a security measure to prevent drill downs
/// from getting access to invalid records. This method is used to speed up the form load time by
/// restricting the first load of all viewable records by applying only verified ranges.
/// </remarks>
private void applyReportDrilldownRange()
{
    if (this.formRun().args() && this.formRun().args().getRequestContextQuery())
    {
        UrlGenerator generator = new UrlGenerator();
        generator.ParseRequestQueryParameter(this.formRun().args().getRequestContextQuery());

        RequestQueryParameterCollection parameterCollection = generator.RequestQueryParameterCollection;

        while (parameterCollection.moveNext())
        {
            RequestQueryParameter parameter = parameterCollection.current();

            if (tableStr(GeneralJournalEntrySim_IT) == parameter.DataSource)
            {
                int fieldCount = parameter.getFieldValueCount();

                for (int i = 0; i < fieldCount; i++)
                {
                    var fieldName = parameter.getField(i);
                    var fieldValue = parameter.getValue(i);

                    if (fieldName && fieldValue)
                    {
                        QueryBuildRange drillDownFilter;

                        switch (fieldName)
                        {
                            case fieldStr(GeneralJournalEntrySim_IT, RecId):
                                drillDownFilter = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(GeneralJournalEntrySim_IT, RecId));
                                drillDownFilter.value(fieldValue);

                                break;

                            case fieldStr(GeneralJournalEntrySim_IT, SubledgerVoucher):
                                drillDownFilter = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(GeneralJournalEntrySim_IT, SubledgerVoucher));
                                drillDownFilter.value(fieldValue);

                                break;

                            case fieldStr(GeneralJournalEntrySim_IT, SubledgerVoucherDataAreaId):
                                drillDownFilter = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(GeneralJournalEntrySim_IT, SubledgerVoucherDataAreaId));
                                drillDownFilter.value(fieldValue);

                                break;

                            case fieldStr(GeneralJournalEntrySim_IT, JournalNumber):
                                drillDownFilter = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(GeneralJournalEntrySim_IT, JournalNumber));
                                drillDownFilter.value(fieldValue);

                                break;
                        }
                    }
                }

                break;
            }
        }
    }
}

public int selectionChange()
{
    int ret;

    ret = super();
    if (ret)
    {
        GeneralJournalEntrySim_IT_ds.executeQuery();
    }

    return ret;
}
