[Form]
public class LedgerVoucherType_CN extends FormRun
{
    SysDictEnum     dictEnumRestrictionType;
    NumberSeqScope  scope;
    private boolean isLedgerAccountRestrictionV2FeatureEnabled;

}

public void init()
{
    isLedgerAccountRestrictionV2FeatureEnabled = LedgerAccountRestrictionV2Feature_CN::isEnabled();

    super();

    scope = NumberSeqScopeFactory::createDataAreaScope();

    dictEnumRestrictionType = new SysDictEnum(enumnum(LedgerVoucherRestrictionType_CN));

    if (LedgerParameters::find().ChineseVoucher_CN == NoYes::No)
    {
        voucherType_ds.allowEdit(false);
        voucherType_ds.allowCreate(false);
        voucherType_ds.allowDelete(false);
        accountRestriction_ds.allowEdit(false);
        accountRestriction_ds.allowCreate(false);
        accountRestriction_ds.allowDelete(false);
        accountRestrictionV2_ds.allowEdit(false);
        accountRestrictionV2_ds.allowCreate(false);
        accountRestrictionV2_ds.allowDelete(false);
    }

    if (isLedgerAccountRestrictionV2FeatureEnabled)
    {
        ImpactedAccountTab.visible(false);
        accountRestriction_ds.linkType(FormLinkType::Passive);
    }
    else
    {
        ImpactedAccountsV2Tab.visible(false);
        accountRestrictionV2_ds.linkType(FormLinkType::Passive);
    }
}

public void ledgerJournalNameLookup(
    Object              _ctrl,
    LedgerJournalType   _journalType    = LedgerJournalType::Daily)
{
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tablenum(LedgerJournalName), _ctrl);
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource = query.addDataSource(tablenum(LedgerJournalName));
    QueryBuildRange         queryBuildRangeJournalType = queryBuildDataSource.addRange(fieldnum(LedgerJournalName, JournalType));

    sysTableLookup.addLookupfield(fieldnum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupfield(fieldnum(LedgerJournalName, Name));

    queryBuildRangeJournalType.value(queryValue(_journalType));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

private void enableAccountRestrictionV2Fields()
{
    AccountRestrictionV2_ds.object(fieldNum(LedgerAccountRestrictionV2_CN, AccountCode)).allowEdit(AccountRestrictionV2.AccountType != LedgerJournalACType::Ledger);
    AccountRestrictionV2_ds.object(fieldNum(LedgerAccountRestrictionV2_CN, AccountRelation)).allowEdit(AccountRestrictionV2.AccountCode == TableGroupAll::GroupId);
    AccountRestrictionV2_ds.object(fieldNum(LedgerAccountRestrictionV2_CN, LedgerDimension)).allowEdit(AccountRestrictionV2.AccountCode == TableGroupAll::Table);

    AccountRestrictionV2_ds.object(fieldNum(LedgerAccountRestrictionV2_CN, LedgerDimension)).mandatory(AccountRestrictionV2.AccountCode == TableGroupAll::Table);
    AccountRestrictionV2_ds.object(fieldNum(LedgerAccountRestrictionV2_CN, AccountRelation)).mandatory(AccountRestrictionV2.AccountCode == TableGroupAll::GroupId);
}

public void write()
{
    LedgerVoucherType_CN    this_Orig = VoucherType.orig();

    super();

    if (VoucherType.DefaultType != this_Orig.DefaultType
        && VoucherType.DefaultType)
    {
        element.doRefresh();
    }
}

public void modified()
{
    LedgerVoucherType_CN    this_Orig = voucherType.orig();

    super();

    if (voucherType.DefaultType != this_Orig.DefaultType
        && voucherType.DefaultType)
    {
        // Rules for voucher type will be ignored
        info(strfmt("@GLS60251", voucherType.Id));
    }
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    NumberSequenceCode code = _formReferenceControl.filterValue(
        AbsoluteFieldBinding::construct(fieldStr(NumberSequenceTable, NumberSequence),
        tableStr(NumberSequenceTable))).value();

    // Do not call super as we're providing our own disambiguation logic.
    // resolvedRecord = super(_formReferenceControl);

    return NumberSequenceTable::findByNaturalKey(code, scope.getId(true));
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    // Lookup number sequence(scope = company and company is current company)
    return NumberSequenceTable::lookupReference(_formReferenceControl);
}

