public void init()
{
    LedgerAccountRestriction_CN ledgerAccountRestriction;
    LedgerAccountRestrictionV2_CN ledgerAccountRestrictionV2;

    super();

    if (isLedgerAccountRestrictionV2FeatureEnabled)
    {
        insert_recordset AccountRestrictionTypeTmp
            (VoucherType, RestrictionType)
            select LedgerVoucherType, RestrictionType from ledgerAccountRestrictionV2
                group by LedgerVoucherType, RestrictionType;
    }
    else
    {
        insert_recordset AccountRestrictionTypeTmp
            (VoucherType, RestrictionType)
            select LedgerVoucherType, RestrictionType from ledgerAccountRestriction
                group by LedgerVoucherType, RestrictionType;
    }
}

public void delete()
{
    LedgerAccountRestriction_CN ledgerAccountRestriction;
    LedgerAccountRestrictionV2_CN ledgerAccountRestrictionV2;
    RecId                           accountRestrictionTypeTmpRecId = AccountRestrictionTypeTmp.RecId;
    LedgerVoucherTypeRefRecId_CN    ledgerVoucherTypeRecId;
    LedgerVoucherRestrictionType_CN voucherRestrictionType;

    if (accountRestrictionTypeTmpRecId)
    {
        ledgerVoucherTypeRecId = AccountRestrictionTypeTmp.VoucherType;
        voucherRestrictionType = AccountRestrictionTypeTmp.RestrictionType;
    }

    super();

    if (accountRestrictionTypeTmpRecId)
    {
        if (isLedgerAccountRestrictionV2FeatureEnabled)
        {
            delete_from ledgerAccountRestrictionV2
                where ledgerAccountRestrictionV2.LedgerVoucherType == ledgerVoucherTypeRecId
                    && ledgerAccountRestrictionV2.RestrictionType == voucherRestrictionType;
        }
        else
        {
            delete_from ledgerAccountRestriction
                where ledgerAccountRestriction.LedgerVoucherType == ledgerVoucherTypeRecId
                    && ledgerAccountRestriction.RestrictionType == voucherRestrictionType;
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    if (!AccountRestrictionTypeTmp.RecId)
    {
        AccountRestrictionTypeTmp.RestrictionType = LedgerVoucherRestrictionType_CN::VoucherInclude;
    }

    return ret;
}

