public void lookup()
{
    switch (accountRestriction.AccountType)
    {
        case LedgerJournalACType::Bank:
            BankAccountTable::lookupBankAccount(this);
            break;
        case LedgerJournalACType::Cust:
            CustTable::lookupCustomer(this);
            break;
        case LedgerJournalACType::FixedAssets:
            AssetTable::lookupAccountNum(this);
            break;
        case LedgerJournalACType::Project:
            ProjTable::lookupProjId(this, accountRestriction);
            break;
        case LedgerJournalACType::Vend:
            VendTable::lookupVendor(this);
            break;
        default:
            super();
            break;
    }
}

public boolean checkUseCustomLookup(int _accountTypeEnumValue, int _secondaryAccountTypeEnumValue)
{
    boolean returnValue;

    switch (_accountTypeEnumValue)
    {
        case enum2int(LedgerJournalACType::Bank):
        case enum2int(LedgerJournalACType::Cust):
        case enum2int(LedgerJournalACType::FixedAssets):
        case enum2int(LedgerJournalACType::Project):
        case enum2int(LedgerJournalACType::Vend):
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

