public class MCRBrokerClaims extends FormRun
{
    private const str allStatus = "@SYS80094";
    private const str openStatus = "@SYS80095";
    private const str closedStatus = "@SYS67267";

    private FormObservableLink markedAccrualsObservableLink = new FormObservableLink();
    private tmpFrmVirtual markedRecs;
    private tmpFrmVirtual headerMarkedFlag;

    private QueryBuildRange GroupBy_statusRange, GroupBy_BrokerRange, GroupBy_SalesIDRange, GroupBy_dateRange;
    private QueryBuildRange Filter_statusRange, Filter_BrokerRange, Filter_dateRange;

    // Going to leave this code and tab on the form for future implementation
    // However, for now only the SalesID mode will be used

    // If false, form is loaded is SalesID mode - Groups header by salesID
    // If true, form is loaded in Broker mode - Groups header by broker
    private Boolean groupByBroker;

