/// <summary>
///     Deletes everything from the temporary table, which clears
///     the mark flag from all records in the grids.
/// </summary>
/// <param name="_salesID">
/// The sales ID of the records that should be deleted; optional.
/// </param>
void clearMarked(SalesID _salesID = '')
{
    delete_from markedRecs
        where (_salesID && markedRecs.SalesId == _salesID)
                || !_salesID;
    delete_from headerMarkedFlag
        where (_salesID && headerMarkedFlag.SalesId == _salesID)
                || !_salesID;

    element.setButtonProperties(false);
}

/// <summary>
/// Finds a record stored in the tmp table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <returns>true if a record is found in the markedRecs temporary table, false otherwise.</returns>
private boolean findMarkedRec(recId _recId)
{
    select firstOnly markedRecs where markedRecs.RecordNo == _recId;
    return markedRecs.RecId != 0;
}

public void init()
{
    Args formArgs;

    if (element.args())
    {
        formArgs = element.args();
        if (formArgs.parmEnum() && formArgs.parmEnumType() == enumNum(NoYes))
        {
            groupByBroker = formArgs.parmEnum();
        }
    }

    super();

    // Everything defaults to showing the SO tab and grouping by SalesID
    // Need to execute extra logic to group by broker
    if (groupByBroker)
    {
        TabPg_Overview_SalesOrder.visible(false);
        MCRBrokerAccrual_Lines_BrokerID.visible(false);

        TabPg_Overview_Broker.visible(true);
        MCRBrokerAccrual_Lines_SalesId.visible(true);
    }

    // ctrlSplitHori s the split group Master is the Group prior to the form split group

    element.updateDesign(InventDimFormDesignUpdate::Init);

    Status.add(allStatus);
    Status.add(openStatus);
    Status.add(closedStatus);

    Status.selection(Status.find(openStatus));
}

