/// <summary>
///     Loops over all displayed records,
///     processing only those that are open and adds them to
///     the line and header temporary tables.
/// </summary>
/// <param name="_salesID">
/// The sales ID of the broker accrual records to loop through.
/// </param>
void markHeader(SalesID _salesID = '')
{
    queryRun        queryRun;
    MCRBrokerAccrual markAllBrokerAccrual;
    AmountMST       amtToApprove;
    Query           localQuery;

    if (Status.valueStr() != closedStatus)
    {
        if (_salesID)
        {
            localQuery = new Query(filterQuery);
            localQuery.dataSourceTable(TableNum(MCRBrokerAccrual))
                .rangeField(fieldNum(MCRBrokerAccrual, SalesID))
                .value(MCRBrokerAccrual_Header.SalesId);

            queryRun = new QueryRun(localQuery);
        }
        else
        {
            queryRun = new QueryRun(filterQuery);
        }

        while (queryRun.next())
        {
            markAllBrokerAccrual = queryRun.get(tableNum(MCRBrokerAccrual));
            if (markAllBrokerAccrual.Status == MCROpenClosed::Open)
            {
                select firstonly markedRecs
                    where markedRecs.RecordNo == markAllBrokerAccrual.RecId;

                // Only insert into the temp table if it is not already part of it
                if (!markedRecs)
                {
                    amtToApprove = markAllBrokerAccrual.calcDfltAmtToApprove();
                    element.writeMarkedRecs(markAllBrokerAccrual.RecId, markAllBrokerAccrual.SalesId, amtToApprove, markAllBrokerAccrual.BrokerID);
                }
            }
        }

        element.setButtonProperties(false);
    }
}

void setButtonProperties(boolean _fromTabChange = true)
{
    if (!_fromTabChange)
    {
        select firstonly markedRecs;

        Approve.enabled(markedRecs.RecId);
        Close.enabled(markedRecs.RecId);

        Header_ApproveAll.enabled(markedRecs.RecId);
        Header_CloseAll.enabled(markedRecs.RecId);
    }
    else
    {
        Transactions.text("@SYS9305");
        if (TabPg_Lines_Overview.isActivePage() ||
            TabPg_Lines_General.isActivePage()  ||
            TabPg_Lines_DefaultDim.isActivePage())
        {
            Transactions.enabled(true);
        }
        else if (TabPg_Details.isActivePage())
        {
            Transactions.enabled(false);
        }
        else if (TabPg_Differentials.isActivePage())
        {
            Transactions.enabled(false);
        }
        else if (TabPg_VendTrans.isActivePage())
        {
            Transactions.enabled(false);
        }
    }
}

void setDateRange()
{
    FromDate fromDate;
    ToDate   toDate;

    fromDate = Filter_FromDate.dateValue();
    toDate   = Filter_ToDate.dateValue();

    if (!fromDate && !toDate)
    {
        GroupBy_dateRange.value('');
        Filter_dateRange.value('');
    }
    else
    {
        GroupBy_dateRange.value(date2StrUsr(fromDate, DateFlags::FormatAll) + '..' + date2StrUsr(toDate, DateFlags::FormatAll));
        Filter_dateRange.value(date2StrUsr(fromDate, DateFlags::FormatAll) + '..' + date2StrUsr(toDate, DateFlags::FormatAll));
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Setup::newFromForm(element);
            }
        // No break
        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(salesLine.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;
        default: throw error(strfmt("@SYS54195", funcname()));
    }
}

public void updateMCRBrokerClaimRecordButtons()
{
    if (TabPg_VendTrans.isActivePage())
    {
        Transactions.enabled(false);
        VendTransactions.enabled(MCRBrokerClaim.vendInvoiceJour().RecId);
        BrokerInvoice.enabled(MCRBrokerClaim.VendInvoiceInfoTableTableRefId != '');
    }
    else if (TabPg_Differentials.isActivePage())
    {
        LedgerJournalTable.enabled((MCRBrokerClaim_Differentials.LJTransClaimRecID != 0));
    }
}

void writeMarkedHeaders(SalesID _salesID)
{
    select firstonly headerMarkedFlag
        where headerMarkedFlag.SalesId == _salesID;

    if (!headerMarkedFlag)
    {
        headerMarkedFlag.SalesId = _salesID;
        if (headerMarkedFlag.validateWrite())
        {
            headerMarkedFlag.insert();
        }
        else
        {
            throw error("@SYS104318");
        }
    }
}

/// <summary>
/// Writes to the temporary tables.
/// </summary>
/// <param name="_recId">
/// The record ID to set on the temporary record.
/// </param>
/// <param name="_salesID">
/// The sales ID to set on the temporary record.
/// </param>
/// <param name="_amt">
/// The amount to set on the temporary record.
/// </param>
/// <param name="_brokerId">
/// The broker id to set on the temporary record.
/// </param>
void writeMarkedRecs(recId _recId, SalesID _salesID, real _amt, MCRBrokerVendAccount _brokerId)
{
    markedRecs.RecordNo     = _recId;
    markedRecs.SalesId      = _salesID;
    markedRecs._Real2       = _amt;
    markedRecs._MCRString1  = _brokerId;
    markedRecs._MCRString2  = MCRBrokerAccrual::findByRecID(_recId).BrokerContractID;
    markedRecs.write();

    element.writeMarkedHeaders(_salesID);
}

private void refreshMCRBrokerAccrual()
{
    MCRBrokerAccrual_Header_ds.research();
    MCRBrokerAccrual_Lines_ds.research();
}

public void init()
{
    QueryBuildDataSource GroupByQueryBuildDataSource, FilterQueryBuildDataSource;

    super();

    GroupByQueryBuildDataSource = this.query().dataSourceName(MCRBrokerAccrual_Header_ds.name());

    GroupBy_statusRange  = GroupByQueryBuildDataSource.addRange(fieldNum(MCRBrokerAccrual, Status));
    GroupBy_BrokerRange  = GroupByQueryBuildDataSource.addRange(fieldNum(MCRBrokerAccrual, BrokerID));
    GroupBy_SalesIDRange = GroupByQueryBuildDataSource.addRange(fieldNum(MCRBrokerAccrual, SalesID));
    GroupBy_dateRange    = GroupByQueryBuildDataSource.addRange(fieldNum(MCRBrokerAccrual, CreatedDateTime));

    // Since group by's cannoter be removed from a query, and the query of the header
    //      is used when both marking all records and checking the header grid's mark field
    //      the query of the Header data source is copied along with the range variables.
    filterQuery = new Query(this.query());

    FilterQueryBuildDataSource = filterQuery.dataSourceTable(TableNum(MCRBrokerAccrual));

    Filter_statusRange  = FilterQueryBuildDataSource.rangeField(fieldNum(MCRBrokerAccrual, Status));
    Filter_BrokerRange  = FilterQueryBuildDataSource.rangeField(fieldNum(MCRBrokerAccrual, BrokerID));
    Filter_dateRange    = FilterQueryBuildDataSource.rangeField(fieldNum(MCRBrokerAccrual, CreatedDateTime));

    GroupBy_statusRange.value(openStatus);

    if (groupByBroker)
    {
        GroupByQueryBuildDataSource.addGroupByField(fieldNum(MCRBrokerAccrual, BrokerID));
    }
    else
    {
        GroupByQueryBuildDataSource.addGroupByField(fieldNum(MCRBrokerAccrual, SalesID));
        GroupByQueryBuildDataSource.addOrderByField(fieldNum(MCRBrokerAccrual, SalesID));
    }

    GroupByQueryBuildDataSource.addGroupByField(fieldNum(MCRBrokerAccrual, CustAccount));

    GroupByQueryBuildDataSource.addSelectionField(fieldNum(MCRBrokerAccrual, QualifiedAmt), SelectionField::Sum);
    GroupByQueryBuildDataSource.addSelectionField(fieldNum(MCRBrokerAccrual, ApprovedAmt), SelectionField::Sum);
    GroupByQueryBuildDataSource.orderMode(OrderMode::GroupBy);

    this.cacheAddMethod(tableMethodStr(MCRBrokerAccrual, displayDifference_BySalesID));
}

/// <summary>
/// Marks the transactions in the grid.
/// </summary>
/// <param name="_set">
/// Indicates the record should be set.
/// </param>
/// <param name="_MCRBrokerAccrual_Header">
/// The record that should be marked.
/// </param>
/// <param name="_markTrans">
/// The value to mark the record.
/// </param>
/// <returns>
/// The current value of the record.
/// </returns>
public edit NoYes markTrans(
    boolean _set,
    MCRBrokerAccrual _MCRBrokerAccrual_Header,
    NoYes _markTrans)
{
    NoYes marked = NoYes::No;

    if (_set)
    {
        // If it is not already marked, then assign the approval amount the default
        if (_markTrans == NoYes::Yes)
        {
            element.markHeader(MCRBrokerAccrual_Header.SalesId);
            marked = NoYes::Yes;
        }
        else
        {
            element.clearMarked(_MCRBrokerAccrual_Header.SalesId);
        }
    }
    else
    {
        select firstonly headerMarkedFlag
            where headerMarkedFlag.SalesId == _MCRBrokerAccrual_Header.SalesId;

        if (headerMarkedFlag)
        {
            marked = NoYes::Yes;
        }
    }

    return  marked;
}

