/// <summary>
///     Returns the percent settled of this customer transaction.
/// </summary>
/// <param name="_set">
/// Indicates the record should be set.
/// </param>
/// <param name="_MCRBrokerAccrual_Lines">
/// The record that should be set.
/// </param>
/// <param name="_amountToApprove">
/// The amount to approve value to set on the record.
/// </param>
/// <returns>
/// the percent settled of this customer transaction.
/// </returns>
/// <remarks>
///     If <c>AmountMST</c> is 0, 0 is returned.
/// </remarks>
Edit AmountCur editAmtToApprove(boolean _set, MCRBrokerAccrual _MCRBrokerAccrual_Lines, AmountMST _amountToApprove)
{
    CustTrans custTrans = CustTrans::find(_MCRBrokerAccrual_Lines.CustTransRecId);
    AmountMST retAmt;

    if (_set)
    {
        if (_amountToApprove != 0)
        {
            // If the signs don't match then flip the sign for _amountToApprove
            if (sign(_MCRBrokerAccrual_Lines.QualifiedAmt) != sign(_amountToApprove))
            {
                _amountToApprove = -_amountToApprove;
            }
            // If the line has already been marked, it is just updated w/ the new value
            element.writeMarkedRecs(_MCRBrokerAccrual_Lines.RecId,
                                    _MCRBrokerAccrual_Lines.SalesId,
                                    _amountToApprove,
                                    _MCRBrokerAccrual_Lines.BrokerID);
            retAmt = _amountToApprove;

            element.setButtonProperties(false);
        }
        else
        {
            this.markTrans(true, _MCRBrokerAccrual_Lines, NoYes::No);
        }
    }
    else
    {
        if (element.findMarkedRec(_MCRBrokerAccrual_Lines.RecId))
        {
            retAmt = markedRecs._Real2;
        }
        else
        {
            retAmt = _MCRBrokerAccrual_Lines.calcDfltAmtToApprove();
        }
    }

    return retAmt;
}

/// <summary>
/// Marks the transactions in the grid.
/// </summary>
/// <param name="_set">
/// Indicates the record should be set.
/// </param>
/// <param name="_MCRBrokerAccrual_Lines">
/// The record that should be marked.
/// </param>
/// <param name="_markTrans">
/// The value to mark the record.
/// </param>
/// <returns>
/// The current value of the record.
/// </returns>
public edit NoYes markTrans(
    boolean _set,
    MCRBrokerAccrual _MCRBrokerAccrual_Lines,
    NoYes _markTrans)
{
    markedAccrualsObservableLink.observe();

    if (_set)
    {
        // If it is not already marked, then assign the approval amount the default
        if (_markTrans == NoYes::Yes)
        {
            element.writeMarkedRecs(_MCRBrokerAccrual_Lines.RecId, _MCRBrokerAccrual_Lines.SalesId,
                                    _MCRBrokerAccrual_Lines.calcDfltAmtToApprove(),
                                    _MCRBrokerAccrual_Lines.BrokerID);
        }
        else
        {
            delete_from markedRecs where
                markedRecs.RecordNo == _MCRBrokerAccrual_Lines.recid;
            delete_from headerMarkedFlag where
                headerMarkedFlag.SalesId == _MCRBrokerAccrual_Lines.SalesId;
        }

        element.setButtonProperties(false);
    }

    return element.findMarkedRec(_MCRBrokerAccrual_Lines.RecId);
}

