/// <summary>
///     Changes the range for the header grid based on what is chosen.
/// </summary>
/// <returns>
/// The new value of the selection change.
/// </returns>
public int selectionChange()
{
    int ret;

    ret = super();

    if (ret)
    {
        switch (this.valueStr())
        {
            case allStatus:

                GroupBy_statusRange.value('');
                Filter_statusRange.value('');
                break;

            case openStatus:

                GroupBy_statusRange.value(openStatus);
                Filter_statusRange.value(openStatus);
                break;

            case closedStatus:

                GroupBy_statusRange.value(closedStatus);
                Filter_statusRange.value(closedStatus);
                break;

            default:

                GroupBy_statusRange.value('');
                Filter_statusRange.value('');
                break;
        }

        MCRBrokerAccrual_Header_ds.executeQuery();
    }

    return ret;
}

