    {
        vendInvoiceInfoTable = VendInvoiceInfoTable::find(MCRBrokerClaim.VendInvoiceInfoTableParmId,
            MCRBrokerClaim.VendInvoiceInfoTableTableRefId);

        if (vendInvoiceInfoTable)
        {
            args = new Args();
            args.caller(element);

            args.record(vendInvoiceInfoTable);
            menuFunction = new MenuFunction(
                menuitemDisplayStr(VendInvoiceInfoListPage),
                    MenuItemType::Display);
            menuFunction.run(args);
        }
    }
}

public void pageActivated()
{
    super();

    element.setButtonProperties();
}
