[Form]
/// <summary>
///     The <c>MCRCCAuthManagement</c> form is Responsible for activities related to credit card payment authorizations.
/// </summary>
public class MCRCCAuthManagement extends FormRun
{
    MCRCustPaymStatus               formStatus;
    str                             declinedText;
    str                             pendingAuthText;
    boolean                         installmentPayments;

    QueryBuildRange                 paymentStatusRange;

    #define.Installment('Installment')

}

/// <summary>
///     Initializes all the member variables for the form.
/// </summary>
/// <remarks>
///     Hide status fields based on order management parameters.
/// </remarks>
public void init()
{
    super();

    // Show/Hide installment billing fields
    if (element.args().parm() == #Installment)
    {
        installmentPayments = true;
    }
    else
    {
        installmentPayments = false;
    }

    formStatus = MCRCustPaymStatus::Declined;
    declinedText = "@SYS34720";
    pendingAuthText = "@MCR22264";

    CustPaymStatus.add(declinedText);
    CustPaymStatus.add(pendingAuthText);
    CustPaymStatus.text(declinedText);

    CreditCardAuthTrans_ApprovalExpired.visible(RetailNonRecurringTokenFeatureExposure::isEnabled());
    CreditCardGroup1_M_displayTokenScope.visible(RetailNonRecurringTokenFeatureExposure::isEnabled());
    CreditCardAuthTrans_ApprovalLengthDays.visible(RetailNonRecurringTokenFeatureExposure::isEnabled());
    CreditCardGroup1_M_Amount.visible(RetailNonRecurringTokenFeatureExposure::isEnabled());
}

void logCardViewedEvent()
{
    MCRCustPaymEvent custPaymEvent;

    select firstOnly RecId from custPaymEvent
        where custPaymEvent.TableId == MCRCustPaymTable.TableId
            && custPaymEvent.RecId == MCRCustPaymTable.RecId
            && custPaymEvent.EventType == MCRPaymentEventType::CardDataViewed;

    if (!custPaymEvent)
    {
        MCRCustPaymEvent::insertPaymEvent(MCRPaymentEventType::CardDataViewed, MCRCustPaymTable, CreditCardAuthTrans);
    }
}

/// <summary>
/// Prompts the user for a credit card authorization code.
/// </summary>
/// <returns>
/// The user entered credit card authorization code.
/// </returns>
public str promptAuthCode()
{
    Dialog dialog;
    DialogField dialogField;

    dialog = new Dialog("@MCR20661");
    dialogField = dialog.addField(extendedTypeStr(CreditCardAuthorization), "@MCR20662");

    if (dialog.run() && dialogField.value() == "")
    {
        Box::info("@MCR20663");
    }

    return dialogField.value();
}

/// <summary>
/// Prompts the user for a credit card decline reason code.
/// </summary>
/// <returns>
/// The credit card decline reason code.
/// </returns>
public str promptDeclineReasonCode()
{
    Dialog dialog;
    dialogField dialogField;

    dialog = new Dialog("@MCR20625");
    dialogField = dialog.addField(extendedTypeStr(CreditCardAuthorization), "@MCR20626");

    if (dialog.run() && dialogField.value() == "")
    {
        Box::info("@MCR20624");
    }

    return dialogField.value();
}

/// <summary>
///     Submits the active credit card payment for authorization.
/// </summary>
void submitForAuthorization()
{
    // resubmit for authorization
    MCRCustPaymTotals::reprocessPayment(MCRCustPaymTable);

    CreditCardAuthTrans_ds.research(true);
}

public int active()
{
    int ret;

    ret = super();

    element.logCardViewedEvent();

    return ret;
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildDataSource queryBuildDataSource_Installment;
    RetailTenderTypeTable retailTenderTypeTable;

    super();

    queryBuildDataSource = this.queryBuildDataSource();
    // Sort the data by date, sales id, invoice id
    queryBuildDataSource.addSortField(fieldnum(MCRCustPaymTable, CreatedDateTime));
    queryBuildDataSource.addSortField(fieldnum(MCRCustPaymTable, RecId));
    // Filter out journal line records
    queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, RefTableID)).value(SysQuery::valueNot(tableNum(ledgerJournalTrans)));
    paymentStatusRange = queryBuildDataSource.addRange(fieldnum(MCRCustPaymTable, Status));

    select firstonly tenderTypeId from retailTenderTypeTable
        where retailTenderTypeTable.defaultFunction == RetailTenderFunction::Customer;

    if (retailTenderTypeTable)
    {
        // Filter out customer account payments
        queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, TenderTypeId)).value(SysQuery::valueNot(retailTenderTypeTable.tenderTypeId));
    }

    // Don't pick up refunds
    queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, Amount)).value(SysQuery::value('>0'));

    // Display only installment payments
    if (installmentPayments)
    {
        queryBuildDataSource_Installment = queryBuildDataSource.addDataSource(tableNum(MCRPaymSchedLink));
        queryBuildDataSource_Installment.relations(true);
        queryBuildDataSource_Installment.addRange(fieldNum(MCRPaymSchedLink, CustPaymRefRecId)).value(SysQuery::valueNot(0));
        queryBuildDataSource_Installment.addRange(fieldNum(MCRPaymSchedLink, PlanTableRecId)).value(SysQuery::valueNot(0));
    }

    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, getCurrency));
    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayOrderId));
}

display internal str displayTokenScope()
{
    MCRCustPaymTable curMcrCustPaymRec = MCRCustPaymTable_ds.cursor();

    if (curMcrCustPaymRec && curMcrCustPaymRec.PaymInfoTableId == tableNum(CreditCardCust))
    {
        CreditCardCust creditCardRec = CreditCardCust::find(curMcrCustPaymRec.PaymInfoRecId);
        if (creditCardRec)
        {
            // we treat unknown scope as customer scope
            RetailTokenScope displayScope = (creditCardRec.TokenScope == RetailTokenScope::Unknown) ? RetailTokenScope::Customer : creditCardRec.TokenScope;
            return enum2Str(displayScope);
        }
    }
    return '';
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.queryBuildDataSource();
    queryBuildDataSource.addRange(fieldnum(CreditCardAuthTrans, ProcessorStatus)).value(SysQuery::valueNot(CreditCardProcessorStatus::Approved));
    queryBuildDataSource.addSortField(fieldNum(CreditCardAuthTrans, LineNum), SortOrder::Descending);
}

display internal CreditCardPreapprovalLastNoOfDays displayApprovalLengthDays()
{
    CreditCardAuthTrans creditCardAuthTransRec = CreditCardAuthTrans_ds.cursor();

    if (creditCardAuthTransRec)
    {
        return creditCardAuthTransRec.getPreapprovalDurationDays();
    }
    return 0;
}

/// <summary>
///     Prompts the user to manually authorize the active credit card payment.
/// </summary>
void clicked()
{
    str strAuthCode;
    MCRCustPaym custPaym;

    // prompt user for authorization code
    strAuthCode = element.promptAuthCode();

    // if the user provided an auth code and the same payment is still active, //  manually authorize
    if (strAuthCode)
    {
        if (MCRCustPaymTable.CustPaymType)
        {
            ttsBegin;

            // manually authorize credit card payment
            custPaym = MCRCustPaym::construct(mcrCustPaymTable);
            custPaym.processPaymManually(strAuthCode);

            ttsCommit;

            // refresh related data
            CreditCardAuthTrans_ds.executeQuery();
            MCRCustPaymTable_ds.executeQuery();
            SalesTable_ds.executeQuery();
        }
    }
}

/// <summary>
/// Prompts the user for a credit card authorization code.
/// </summary>
/// <returns>
/// The user entered credit card authorization code.
/// </returns>
public str promptAuthCode()
{
    Dialog dialog;
    DialogField dialogField;

    dialog = new Dialog("@MCR20661");
    dialogField = dialog.addField(extendedTypeStr(CreditCardAuthorization), "@MCR20662");

    if (dialog.run() && dialogField.value() == "")
    {
        Box::info("@MCR20663");
    }

    return dialogField.value();
}

/// <summary>
///     Prompts the user to manually decline the active credit card payment.
/// </summary>
void clicked()
{
    str strDeclineReasonCode;

    RecID paymRecID;
    RecID authRecID;

    MCRCustPaym custPaym;

    // remember the active payment and auth records
    paymRecID = MCRCustPaymTable.RecId;
    authRecID = CreditCardAuthTrans.RecId;

    // prompt user for decline reason code
    strDeclineReasonCode = element.promptDeclineReasonCode();

    // if the user provided a reason code and the same payment is still active, //  manually decline
    if (strDeclineReasonCode
        && paymRecID == MCRCustPaymTable.RecId
        && authRecID == CreditCardAuthTrans.RecId)
    {
        if (MCRCustPaymTable.CustPaymType)
        {
            ttsBegin;

            // manually decline authorization record
            custPaym = MCRCustPaym::construct(MCRCustPaymTable);
            custPaym.processPaymManually(strDeclineReasonCode, false);

            ttsCommit;

            // refresh data
            MCRCustPaymTable_ds.executeQuery();
            CreditCardAuthTrans_ds.executeQuery();
        }
    }
}

/// <summary>
///     Submits the currently selected credit card payment for authorization.
/// </summary>
void clicked()
{
    element.submitForAuthorization();
}

/// <summary>
///     Cancels the order associated
/// </summary>
void clicked()
{
    MCRSalesOrderCancellation::CancelOrder(SalesTable);
    MCRCustPaymTable_ds.executeQuery();
}

public boolean modified()
{
    boolean ret;
    ret = super();

    formStatus = formStatus == MCRCustPaymStatus::Declined ? MCRCustPaymStatus::NotSubmitted : MCRCustPaymStatus::Declined;
    MCRCustPaymTable_ds.executeQuery();

    return ret;
}
