[Form]
public class MCRCCRefundApproval extends FormRun
{
    MCRCustPaymTable        mcrCustPaymTableSelected;

}

void approveRefund(MCRCustPaymTable _mcrCustPaymTable)
{
    MCRCustPaymTotals mcrCustPaymTotals;
    SalesTable salesTableLocal;

    // Update the status
    _mcrCustPaymTable.Status = _mcrCustPaymTable.IsPrepay ? MCRCustPaymStatus::Paid : MCRCustPaymStatus::Authorized;
    if (_mcrCustPaymTable.validateWrite())
    {
        _mcrCustPaymTable.update();
    }
    else
    {
        throw error("@SYS21628");
    }

    if (_mcrCustPaymTable)
    {
        // Sales order refunds need to be re-run through order submission after
        // approval.
        if (_mcrCustPaymTable.RefTableId == tableNum(SalesTable))
        {
            salesTableLocal = _mcrCustPaymTable.salesTable(true);
            // If the order status is not open, set it before running order submission.
            if (salesTableLocal.SalesStatus != SalesStatus::Backorder)
            {
                salesTableLocal.SalesStatus = SalesStatus::Backorder;
                salesTableLocal.update();
            }
            MCREndOrder::endOrder(salesTableLocal.SalesId);
        }
        else
        {
            mcrCustPaymTotals = MCRCustPaymTotals::construct(_mcrCustPaymTable.RefTableId, _mcrCustPaymTable.RefRecId);
            mcrCustPaymTotals.processPayments(MCRProcessPaymAction::PostOnlyPrepays);
        }
    }
}

void declineRefund(MCRCustPaymTable _mcrCustPaymTable)
{
    // Update the status
    _mcrCustPaymTable.Status = MCRCustPaymStatus::Declined;
    if (_mcrCustPaymTable.validateWrite())
    {
        _mcrCustPaymTable.update();
    }
    else
    {
        throw error("@SYS21628");
    }
}

/// <summary>
/// Authorizes or declines the selected refunds based
/// on parameter.
/// </summary>
/// <param name="_approve">
/// A <c>boolean</c> value that determines whether or not
/// the method should approve or decline the refund.
/// </param>
void processRefund(boolean _approve = true)
{
    MCRCustPaymTable mcrCustPaymTableLocal;

    ttsBegin;
    // Loop through the selected sales order refunds
    for (mcrCustPaymTableLocal = MCRCustPaymTable_ST_ds.getFirst(true) ? MCRCustPaymTable_ST_ds.getFirst(true) : MCRCustPaymTable_ST_ds.cursor();
    mcrCustPaymTableLocal;
    mcrCustPaymTableLocal = MCRCustPaymTable_ST_ds.getnext())
    {
        if (_approve)
        {
            element.approveRefund(mcrCustPaymTableLocal);
        }
        else
        {
            element.declineRefund(mcrCustPaymTableLocal);
        }
    }
    ttsCommit;

    ttsBegin;
    // Loop through the selected free text invoice refunds
    for (mcrCustPaymTableLocal = MCRCustPaymTable_FT_ds.getFirst(true) ? MCRCustPaymTable_FT_ds.getFirst(true) : MCRCustPaymTable_FT_ds.cursor();
    mcrCustPaymTableLocal;
    mcrCustPaymTableLocal = MCRCustPaymTable_FT_ds.getnext())
    {
        if (_approve)
        {
            element.approveRefund(mcrCustPaymTableLocal);
        }
        else
        {
            element.declineRefund(mcrCustPaymTableLocal);
        }
    }
    ttsCommit;

    // refresh the grids
    MCRCustPaymTable_ST_ds.research();
    MCRCustPaymTable_FT_ds.research();
}

boolean promptDeclineDialog()
{
    DialogButton    button;
    button = box::yesNo("@MCR20941" + '\n'+'\n'
                       + "@MCR20942", DialogButton::No, "@MCR20943");

    if (button == DialogButton::Yes)
    {
        return true;
    }
    else
    {
        return false;
    }
}

public void init()
{
    super();
    element.setPaymentStatusRanges(MCRCustPaymStatus::NotSubmitted);
}

public void run()
{
    super();

    SalesOrdersTab.pageActivated();
}

public void setPaymentStatusRanges(MCRCustPaymStatus _status)
{
    element.setPaymentStatusRange(MCRCustPaymTable_ST_ds, _status);
    element.setPaymentStatusRange(MCRCustPaymTable_FT_ds, _status);
}

public void setPaymentStatusRange(FormDataSource _dataSource, MCRCustPaymStatus _status)
{
    QueryBuildDataSource qbds = _dataSource.queryBuildDataSource();
    qbds.clearRange(fieldNum(MCRCustPaymTable, Status));
    qbds.addRange(fieldNum(MCRCustPaymTable, Status)).value(SysQuery::value(_status));
}

public void selectionChanged()
{
    super();

    if (MCRCustPaymTable_ST)
    {
        mcrCustPaymTableSelected = MCRCustPaymTable_ST;
    }
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(MCRCustPaymTable));
    // Only list refunds
    queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, Amount)).value('<0');
    // Only list credit cards
    queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, CustPaymType)).value(SysQuery::value(MCRCustPaymType::CreditCard));

    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayName));
    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayCCNumMasked));
    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayExpirydate));
}

public void selectionChanged()
{
    super();

    if (MCRCustPaymTable_FT)
    {
        mcrCustPaymTableSelected = MCRCustPaymTable_FT;
    }
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(MCRCustPaymTable));
    // Only consider refunds
    queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, Amount)).value('<0');
    // Only list credit cards
    queryBuildDataSource.addRange(fieldNum(MCRCustPaymTable, CustPaymType)).value(SysQuery::value(MCRCustPaymType::CreditCard));

    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayName));
    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayCCNumMasked));
    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayExpirydate));
}

void clicked()
{
    // Only approve if a record is selected
    if (MCRCustPaymTable_ST
        || MCRCustPaymTable_FT)
    {
        element.processRefund();
    }
}

void clicked()
{
    // Only prompt if a record is selected
    if ((MCRCustPaymTable_ST
        || MCRCustPaymTable_FT)
        && element.promptDeclineDialog())
    {
        element.processRefund(false);
    }
}

void clicked()
{
    FormRun paymTable;
    Args formArgs;

    if (mcrCustPaymTableSelected)
    {
        formArgs = new Args();
        if (mcrCustPaymTableSelected.RefTableId == tableNum(SalesTable))
        {
            formArgs.record(SalesTable);
        }
        else
        {
            formArgs.record(CustInvoiceTable);
        }

        paymTable = new MenuFunction(menuItemDisplayStr(MCRCustPayMTable), MenuItemType::Display).create(formArgs);

        paymTable.run();
        paymTable.wait();

        if (mcrCustPaymTableSelected.RefTableId == tableNum(SalesTable))
        {
            MCRCustPaymTable_ST_ds.reread();
            MCRCustPaymTable_ST_ds.refresh();
        }
        else
        {
            MCRCustPaymTable_FT_ds.reread();
            MCRCustPaymTable_FT_ds.refresh();
        }
    }
}

void clicked()
{
    PendingView.enabled(false);
    DeclinedView.enabled(true);
    element.setPaymentStatusRanges(MCRCustPaymStatus::NotSubmitted);
    MCRCustPaymTable_ST_ds.executeQuery();
    MCRCustPaymTable_FT_ds.executeQuery();
}

void clicked()
{
    super();
    PendingView.enabled(true);
    DeclinedView.enabled(false);
    element.setPaymentStatusRanges(MCRCustPaymStatus::Declined);
    MCRCustPaymTable_ST_ds.executeQuery();
    MCRCustPaymTable_FT_ds.executeQuery();
}

