/// <summary>
/// Loads the payment table form with the selected sales order.
/// </summary>
void loadSalesTableForm()
{
    Args args;
    FormRun loadSalesTable;

    args = new Args();
    args.record(currentRecord.salesTable());

    loadSalesTable = new MenuFunction(menuitemdisplayStr(SalesTable), MenuItemType::Display).create(args);
    loadSalesTable.run();
    loadSalesTable.wait();
}

/// <summary>
/// Cancels the sales order.
/// </summary>
void mcrPromptCancelOrder()
{
    MCRSalesOrderCancellation::CancelOrder(SalesTable);

    // Refresh after updates.
    salesTable_ds.executeQuery();
}

public void run()
{
    super();

    element.setOverallDisplays();
    element.updateButtonControls();
}

/// <summary>
/// Sets the values that are displayed to the screen.
/// </summary>
void setOverallDisplays()
{
    totalNumOrders      = MCRCustPaymTable::getOverallTotalOrders(MCRCustPaymStatus::Posted, MCRCustPaymType::Check);
    oldestDate          = MCRCustPaymTable::getOverallOldestDate(MCRCustPaymStatus::Posted, MCRCustPaymType::Check);
}

/// <summary>
/// Disables or enables the button groups depending on
/// whether or not a record is selected.
/// </summary>
void updateButtonControls()
{
    if (SalesOrders.isActivePage())
    {
        if (!MCRCustPaymTableSO)
        {
            ButtonGroup.enabled(false);
        }
        else
        {
            if (!ButtonGroup.enabled())
            {
                ButtonGroup.enabled(true);
            }
        }
    }
    else
    {
        if (!MCRCustPaymTableFT)
        {
            ButtonGroup.enabled(false);
        }
        else
        {
            if (!ButtonGroup.enabled())
            {
                ButtonGroup.enabled(true);
            }
        }
    }
}

/// <summary>
/// Updates the <c>MCRCustPaymTable</c> table records and process the sales order.
/// </summary>
protected void updateMCRCustPaymTable()
{
    MCROrderEventTable MCROrderEventTable;

    currentRecord.checkAuthorized = NoYes::Yes;
    currentRecord.Status = MCRCustPaymStatus::Paid;

    ttsbegin;
    SalesTable.update();
    MCRCustPaymTableSO.update();
    ttscommit;

    MCREndOrder::endOrder(SalesTable.SalesId, false);

    MCROrderEventTable.insertPaymentEvent(MCROrderEventType::CheckHoldRelease, currentRecord,
        strfmt("@MCR24829", currentRecord.Amount));
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(SalesTable))
                    .addRange(fieldNum(SalesTable, SalesStatus))
                    .value(enum2str(SalesStatus::Backorder));
}

public int active()
{
    int ret;

    custNumCheckHolds = 0;
    custOldestCheckHold = DateNull();
    custMaxCheckAmount = 0;

    ret = super();

    return ret;
}

/// <summary>
/// Gets and displays the number of days a payment has been on check hold.
/// </summary>
/// <param name="_MCRCustPaymTable">
/// The customer payment record that has a check hold.
/// </param>
/// <returns>
/// The number of days a payment has been on check hold.
/// </returns>
display MCRDaysOnHold displayDaysOnHold(MCRCustPaymTable _MCRCustPaymTable)
{
    return (MCRCustPaymTable::getDaysSinceCreation(_MCRCustPaymTable.RecId));
}

/// <summary>
/// Returns the oldest check hold date for the selected customer.
/// </summary>
/// <returns>
/// The oldest check hold date for the selected customer.
/// </returns>
display TransDate displayOldestCustDate()
{
    if (!custOldestCheckHold)
    {
        custOldestCheckHold = MCRCustPaymTable::getCustOldestCheckHoldDate(currentRecord.CustAccount);
    }
    return custOldestCheckHold;
}

/// <summary>
/// Filters out orders that don't have a status of over credit limit.
/// </summary>
public void init()
{
    super();

    this.query().dataSourceTable(tableNum(MCRCustPaymTable))
                    .addRange(fieldNum(MCRCustPaymTable, Status))
                    .value(enum2str(MCRCustPaymStatus::Posted));

    this.query().dataSourceTable(tableNum(MCRCustPaymTable))
                    .addRange(fieldNum(MCRCustPaymTable, CustPaymType))
                    .value(enum2str(MCRCustPaymType::Check));

    currentRecord = MCRCustPaymTableSO;
    custNumCheckHolds = 0;
    custOldestCheckHold = DateNull();
    custMaxCheckAmount = 0;

    TotalNumOrders = 0;

    this.cacheAddMethod(tableMethodStr(MCRCustPaymTable, getCurrency));
}

