/// <summary>
/// Gets and returns the name of the customer on the sales order or invoice.
/// </summary>
/// <param name="_MCRCustPaymTable">
/// The customer payment record for which to display the customer name.
/// </param>
/// <returns>
/// The name of the customer on the sales order or invoice.
/// </returns>
display CustName displayCustName(MCRCustPaymTable _MCRCustPaymTable)
{
    if (currentRecord.RefTableId == tableNum(SalesTable))
    {
        return _MCRCustPaymTable.custTable().name();
    }
    else
    {
        return _MCRCustPaymTable.CustInvoiceTable().Name;
    }
}

