void clicked()
{
    if (currentRecord.RefTableId == tableNum(SalesTable))
    {
        element.updateMCRCustPaymTable();

        MCRCustPaymTableSO_ds.executeQuery();
        SalesTable_ds.executeQuery();

        super();
    }
    else if (currentRecord.RefTableId == tableNum(CustInvoiceTable))
    {
        currentRecord.Status = MCRCustPaymStatus::NotSubmitted;
        currentRecord.checkAuthorized = NoYes::Yes;
        ttsbegin;
        MCRCustPaymTableFT.update();
        ttscommit;
        MCRCustPaymTableFT_ds.executeQuery();
        CustInvoiceTable_ds.executeQuery();
    }
    else
    {
        info("@MCR24645");
    }
    custNumCheckHolds = 0;
    custOldestCheckHold = DateNull();
    custMaxCheckAmount = 0;

    MCRCustPaymtableFT_ds.refresh();
    MCRCustPaymTableSO_ds.refresh();
    element.setOverallDisplays();
    element.updateButtonControls();
}

/// <summary>
/// Cancels the related sales order.
/// </summary>
void clicked()
{
    if (SalesTable)
    {
        element.mcrPromptCancelOrder();

        MCRCustPaymTableSO_ds.executeQuery();
        SalesTable_ds.executeQuery();
        element.setOverallDisplays();
        element.updateButtonControls();
    }
    else
    {
        info("@MCR24645");
    }
}

/// <summary>
/// Cancels the sales order that does not have sufficient funds.
/// </summary>
void clicked()
{
    int nonZeroRecs = 0;

    if (MCRCustPaymTableSO)
    {
        nonZeroRecs = MCRCustPaymTable::countNonZeroAmount(currentRecord.RefTableId, currentRecord.RefRecId);

        if (nonZeroRecs == 1)
        {
            if (currentRecord.RefTableId == TableNum(SalesTable))
            {
                MCRSalesOrderCancellation::CancelOrder(SalesTable, false, true);
                MCRCustPaymTableSO_ds.executeQuery();
                SalesTable_ds.executeQuery();
            }

            element.setOverallDisplays();
            element.updateButtonControls();
        }
        else
        {
            warning("@MCR24907");
        }
        super();
    }
    else
    {
        info("@MCR24645");
    }
}

/// <summary>
/// Cancels the customer payment that does not have sufficient funds.
/// </summary>
void clicked()
{
    int             nonZeroRecs = 0;
    SalesTable      salesTableLocal;
    MCRSalesTable   mcrSalesTableLocal;

    if (currentRecord)
    {
        nonZeroRecs = MCRCustPaymTable::countNonZeroAmount(currentRecord.RefTableId, currentRecord.RefRecId);

        if (nonZeroRecs == 1)
        {
            if (currentRecord.RefTableId == TableNum(SalesTable))
            {
                MCRCustPaymTable::cancelPaym(MCRCustPaymTableSO.RecId);
                if (currentRecord.RefTableId == TableNum(SalesTable))
                {
                    TTSBEGIN;
                    salesTableLocal = SalesTable::findRecId(currentRecord.RefRecId,true);
                    mcrSalesTableLocal = salesTableLocal.mcrSalesTable();
                    mcrSalesTableLocal.OutOfBalanceReleased = MCROutOfBalanceReleaseType::None;
                    salesTableLocal.update();
                    TTSCOMMIT;

                    MCRPaymentReview::outOfBalance(MCRCustPaymTotals::construct(salesTableLocal.TableId, salesTableLocal.RecId));
                }
                MCRCustPaymTableSO_ds.executeQuery();
                SalesTable_ds.executeQuery();
            }

            element.setOverallDisplays();
            element.updateButtonControls();
        }
        else
        {
            warning("@MCR24907");
        }
        super();
    }
    else
    {
        info("@MCR24645");
    }
}

public void pageActivated()
{
    super();

    MCRCustPaymTableSO_ds.executeQuery();
    element.setOverallDisplays();

    // Ensure the selected record is used.
    currentRecord = MCRCustPaymTableSO;
    custNumCheckHolds = 0;
    custOldestCheckHold = DateNull();
    custMaxCheckAmount = 0;

    CancelOrder.visible(true);
    NSFCancel.visible(true);
    PaymentsSO.visible(true);
    PaymentsFT.visible(false);
    SalesOrder.visible(true);
    CustTransSO.visible(true);
    CustTransFT.visible(false);
    MCRCheckHoldCustInfoSO.visible(true);
    MCRCheckHoldCustInfoFT.visible(false);
    element.updateButtonControls();
}

