void addNewCardHolder()
{
    CreditCardPaymentCardTokenize   cardTokenize;
    CreditCardProcessors            activeCreditCardProcessors;
    CreditCardCust                  newCreditCardCust;

    activeCreditCardProcessors = CreditCardProcessors::findActiveProcessor();

    cardTokenize = CreditCardPaymentCardTokenize::construct();

    cardTokenize.init(activeCreditCardProcessors.Name);
    cardTokenize.parmCustAccount(mcrCustPaymTable.CustAccount);
    cardTokenize.parmIsSupportSalesOrderScope(true);

    if (!cardTokenize.getFromDialog())
    {
        return;
    }

    newCreditCardCust = CreditCard::addCreditCard(cardTokenize.getXmlString(),
                                                    mcrCustPaymTable.CustAccount,
                                                    activeCreditCardProcessors.RecId);
    if (newCreditCardCust)
    {
        CreditCardCust::scopeCreditCardToken(newCreditCardCust.RecId, RetailTokenScope::SalesOrder);

        mcrCustPaymTable.PaymInfoRecId = newCreditCardCust.RecId;
        mcrCustPaymTable.PaymInfoTableId = newCreditCardCust.TableId;
        mcrCustPaymTable_ds.cacheCalculateMethod(tableMethodStr(MCRCustPaymTable, editCreditCard));
    }
}

/// <summary>
/// Validates if can close based on certain conditions.
/// </summary>
/// <returns>
/// true if the form can close; otherwise, false.
/// </returns>
public boolean canClose()
{
    boolean                 ret;
    MCRContinuityCustLine   mcrContinuityCustLineLocal;

    ret = super();

    // The form is disabled -
    // allow the form to close as the user cannot do anything about the missing values.
    if (ret && element.args().parmEnum() == true)
    {
        return true;
    }

    if (ret)
    {
        // If not paying a 100% up front and payment has not been entered.
        if (!(!mcrContinuityCustHeader.PaymentScheduleId && mcrContinuityCustHeader.BillUpFront) &&
           !mcrCustPaymTable.RecId)
        {
            ret = checkFailed("@MCR12710");
        }
    }

    // Validate that at least one event exists on the ContinuityCustInfo line that has an
    // event with the action code of end.
    if (ret)
    {
        // Only check for an 'end' event if there are events.
        select firstonly RecId from mcrContinuityCustLineLocal
            where mcrContinuityCustLineLocal.InventTransId == mcrContinuityCustHeader.InventTransId;

        if ( mcrContinuityCustLineLocal.RecId
            && !MCRContinuityCustLine::findRecordWithActionCodeEnd(mcrContinuityCustHeader.InventTransId, false))
        {
            ret = checkFailed("@MCR12827");
        }
    }

    return ret;
}

