/// <summary>
/// Writes order events for turning off price override.
/// </summary>
/// <param name="_userId">
/// The <c>UserId</c> of the user who turned off the price override.
/// </param>
void linePriceOverrideNo(UserId _userId)
{
    mcrContinuityCustLine.PriceOverride         = NoYes::No;
    mcrContinuityCustLine.Price                 = 0;
    mcrContinuityCustLine.PriceOverrideReason   = "";
    mcrContinuityCustLine.RetailInfocodeId      = "";
    mcrLinePriceOverrideEventType               = MCROrderEventType::ContinuityCustLinePriceOverrideTurnedOff;
    retailInformationSubcodeId                  = "";
    retailInfocodeId                            = "";
    mcrLinePriceOverrideUserId                  = _userId;
}

/// <summary>
/// Sets the price override flag when we are overriding a price and also writes an orcer event for
/// turning price override on.
/// </summary>
/// <param name="_retailInformationSubcodeId">
/// The <c>RetailInformationSubcodeId</c> value explaining the reason for the override.
/// </param>
/// <param name="_userId">
/// The <c>UserId</c> of the user who performed the price override.
/// </param>
void linePriceOverrideYes(RetailInformationSubcodeId  _retailInformationSubcodeId, UserId _userId = curUserId())
{
    mcrContinuityCustLine.PriceOverride         = NoYes::Yes;
    mcrContinuityCustLine.PriceOverrideReason   = _retailInformationSubcodeId;
    mcrContinuityCustLine.RetailInfocodeId      = RetailMCRChannelTable::findForCurrentUser().mcrPriceOverrideRetailInfocodeId;
    mcrLinePriceOverrideEventType               = MCROrderEventType::ContinuityCustLinePriceOverrideTurnedOn;
    retailInformationSubcodeId                  = _retailInformationSubcodeId;
    retailInfocodeId                            = mcrContinuityCustLine.RetailInfocodeId;
    mcrLinePriceOverrideUserId                  = _userId;
}

/// <summary>
/// Sets the amount due.
/// </summary>
public void run()
{
    MCRLoadContinuityCustInfo   mcrLoadContinuityCustInfo = new MCRLoadContinuityCustInfo();
    MCRContinuityEventId        mcrContinuityEventId = 1;
    CustPaymSched               custPaymSched;
    AmountCur                   salesLineAmount;

    super();

    if (element.args().parm() == #ParmAdd)
    {
        // Only create a payment schedule on headers for bill up front.
        if (mcrContinuityCustHeader.BillUpFront && mcrContinuityCustHeader.PaymentScheduleId)
        {
            // Create the payment schedule.
            MCRContinuityCustHeader::createPaymentSched(mcrContinuityCustHeader);
        }
        mcrLoadContinuityCustInfo.insertLineData(mcrContinuityCustHeader.InventTransId, mcrContinuityCustHeader.ScheduleId, salesLine.ItemId);
    }
    else
    {
        // If in change mode recalculate the schedule lines.
        custPaymSched = CustPaymSched::find(mcrContinuityCustHeader.TableId, mcrContinuityCustHeader.RecId, false);
        salesLineAmount = salesLine.LineAmount+SalesLine::mcrCalcContinuityLineTax(salesLine);
        if (CustPaymSchedLine::mcrGetTotalOfScheduleLines(custPaymSched.RecId)!= salesLineAmount)
        {
            MCRContinuityCustHeader::recalcPaymentSchedLines(mcrContinuityCustHeader);
        }

        if (SalesTable::find(salesLine.SalesId).SalesStatus != SalesStatus::Backorder
            || element.args().parmEnum() == true)
        {
            BodyTab.allowEdit(false);
            grid.enabled(false);
            grid.skip(true);
        }
    }

    if (MCRContinuityScheduleHeader::find(salesLine.mcrSalesLine().ContinuityScheduleId).PromptForPaymSched == false)
    {
        mcrContinuityCustHeader_PaymentScheduleId.enabled(false);
        mcrContinuityCustHeader_PaymentScheduleId.skip(true);
    }
    else
    {
        // Before enabling check if the billing cycle has stared.
        if (element.args().parm() == #ParmAdd)
        {
            mcrContinuityCustHeader_PaymentScheduleId.enabled(true);
            mcrContinuityCustHeader_PaymentScheduleId.skip(false);
        }
        else
        {
            // if the billing cycle is has started and the record is not in add mode payment schedule cannot be changed.
            custPaymSched = CustPaymSched::find(mcrContinuityCustHeader.TableId, mcrContinuityCustHeader.RecId, false);

            if (CustPaymSchedLine::mcrDoesPaymentRecordExist(custPaymSched.RecId))
            {
                mcrContinuityCustHeader_PaymentScheduleId.enabled(false);
                mcrContinuityCustHeader_PaymentScheduleId.skip(true);
            }
            else
            {
                mcrContinuityCustHeader_PaymentScheduleId.enabled(true);
                mcrContinuityCustHeader_PaymentScheduleId.skip(false);
            }
        }
    }

    element.setPaymentControls();
    element.setControls();

    amount.realValue(element.dispSalesPrice());
}

