    mcrContinuityCustLine_SalesQtyUOM.skip(mcrContinuityCustHeader.BillUpFront);

    mcrContinuityCustLine_Price.enabled(!mcrContinuityCustHeader.BillUpFront);
    mcrContinuityCustLine_Price.skip(mcrContinuityCustHeader.BillUpFront);

    mcrContinuityCustLine_PriceOverride.enabled(!mcrContinuityCustHeader.BillUpFront);
    mcrContinuityCustLine_PriceOverride.skip(mcrContinuityCustHeader.BillUpFront);

    mcrContinuityCustLine_PaymentSchedLine.enabled(!mcrContinuityCustHeader.BillUpFront);
    mcrContinuityCustLine_PaymentSchedLine.skip(mcrContinuityCustHeader.BillUpFront);

    mcrContinuityCustLine_NumTimesToRepeat.enabled(!mcrContinuityCustHeader.BillUpFront);
    mcrContinuityCustLine_NumTimesToRepeat.skip(mcrContinuityCustHeader.BillUpFront);

    mcrContinuityCustHeader_PaymentScheduleId.enabled(mcrContinuityCustHeader.BillUpFront);
    mcrContinuityCustHeader_PaymentScheduleId.skip(!mcrContinuityCustHeader.BillUpFront);
}

