/// <summary>
/// Enables or disables payment related form controls.
/// </summary>
void setPaymentControls()
{
    PaymentTabPage.allowEdit(false);

    mcrCustPaymTable_CardTypeId.enabled(false);
    mcrCustPaymTable_CardTypeId.skip(true);

    mcrContinuityCustHeader_PaymentScheduleId.enabled(false);
    mcrContinuityCustHeader_PaymentScheduleId.skip(true);

    paymentSchedules.enabled(false);
    paymentSchedules.skip(true);

    createCCBtn.enabled(false);
    createCCBtn.skip(true);

    creditCard_M_editCreditCard.enabled(false);
    creditCard_M_editCreditCard.skip(true);

    if (mcrContinuityCustHeader.BillUpFront)
    {
        if (mcrContinuityCustHeader.PaymentScheduleId)
        {
            PaymentTabPage.allowEdit(true);

            paymentSchedules.enabled(true);
            paymentSchedules.skip(false);

            mcrContinuityCustHeader_PaymentScheduleId.enabled(true);
            mcrContinuityCustHeader_PaymentScheduleId.skip(false);
        }
    }
    else
    {
        PaymentTabPage.allowEdit(true);
    }

    if (PaymentTabPage.allowEdit()
        && mcrCustPaymTable.TenderTypeId
        && RetailTenderTypeTable::find(mcrCustPaymTable.TenderTypeId).DefaultFunction == RetailTenderFunction::Card)
    {
        creditCard_M_editCreditCard.enabled(true);
        creditCard_M_editCreditCard.skip(false);

        mcrCustPaymTable_CardTypeId.enabled(true);
        mcrCustPaymTable_CardTypeId.skip(false);

        createCCBtn.enabled(true);
        createCCBtn.skip(false);
    }
}

public void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm dimParmLockedRightClick;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl::newFromFormValidDataSource(element, inventDim_ds.name()) as InventDimCtrl_Frm;
            }
            dimParmLockedRightClick.setAllProductDimensions();
            dimParmLockedRightClick.InventSiteIdFlag = true;
            dimParmLockedRightClick.InventLocationIdFlag = true;
            dimParmLockedRightClick.toggleAllInventDim();
            inventDimFormSetup.parmDimParmLockedRightClick(dimParmLockedRightClick);
            inventDimFormSetup.formSetControls(true);

            break;

        case InventDimFormDesignUpdate::Active:
        case InventDimFormDesignUpdate::FieldChange:
            InventDimGroupSetup::newItemId(mcrContinuityCustLine.ItemId).inventDimParmActive(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(false);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

