public void modified()
{
    CustPaymSched custPaymSched;

    super();

    mcrContinuityCustHeader.PaymentScheduleId = mcrContinuityCustHeader_PaymentScheduleId.valueStr();

    custPaymSched = CustPaymSched::find(mcrContinuityCustHeader.TableId, mcrContinuityCustHeader.RecId, true);

    // If payment schedule is modified, update the buffer so the payment
    //  schedule calculation can calculate based on the correct schedule.
    if (custPaymSched.Name != mcrContinuityCustHeader.PaymentScheduleId)
    {
        CustPaymSched::deleteCustPaymSched(mcrContinuityCustHeader.TableId, mcrContinuityCustHeader.RecId);
        MCRContinuityCustHeader::createPaymentSched(mcrContinuityCustHeader);
        MCRContinuityCustHeader::recalcPaymentSchedLines(mcrContinuityCustHeader);
    }
}

