/// <summary>
/// Sets up price override security for the price field based on the user.
/// </summary>
/// <param name="_set">
/// A <c>Boolean</c> value defining whether the security setting
/// should be set for this user or not.
/// </param>
/// <param name="_mcrContinuityCustLine">
/// The <c>MCRContinuityCustLine</c> record for which the prive override
/// security settings should be validated.
/// </param>
/// <param name="_value">
/// The override prive value.
/// </param>
/// <returns>
/// The price on the <c>MCRContinuityCustLine</c> record.
/// </returns>
public edit PriceUnit mcrEditPrice(boolean _set,
                                    MCRContinuityCustLine _mcrContinuityCustLine,
                                    Price _value)
{
    if (_set)
    {
        this.mcrGetOverrideResponse(_mcrContinuityCustLine, fieldNum(MCRContinuityCustLine, Price),
            _value);
    }

    return _mcrContinuityCustLine.Price;
}

/// <summary>
/// Gets the price override reason code from user.
/// </summary>
/// <param name="_mcrContinuityCustLine">
/// The <c>MCRContinuityCustLine</c> record for which the reason code should be retrieved.
/// </param>
/// <param name="_fieldId">
/// The <c>FieldId</c> of the field for which the override is being run.
/// </param>
/// <param name="_newValue">
/// The new value for the prive override.
/// </param>
private void mcrGetOverrideResponse(MCRContinuityCustLine _mcrContinuityCustLine, FieldId _fieldId,
    real _newValue)
{
    boolean                         currentUserPermission, cancelOverride;
    Args                            args;
    container                       userIdReasonCode;
    RetailInformationSubcodeId      retailInformationSubcodeIdLocal;
    real                            origValue;
    SalesPrice                      origPricePerSalesQty;
    MCROrderEventTable              mcrOrderEventTable;
    #define.Cancel('Cancel')

    if (!_fieldId == fieldNum(MCRContinuityCustLine, Price))
    {
        throw error(strFmt("@MCR11956", fieldId2name(mcrContinuityCustLine.TableId,
            _fieldId)));
    }

    origPricePerSalesQty = _mcrContinuityCustLine.Price;
    origValue = _mcrContinuityCustLine.(_fieldId);

    _mcrContinuityCustLine.(_fieldId) = _newValue;

    // only check override parameters if the system parameter is set to false.
    if (!RetailMCRChannelTable::mcrEnableOrderPriceControl(SalesTable::find(_mcrContinuityCustLine.SalesId)))
    {
        element.linePriceOverrideYes('', curUserId());
        mcrOrderEventTable.insertFromContinuityCustLine(mcrContinuityCustHeader.SalesId,
                                                     mcrLinePriceOverrideEventType,
                                                     curUserId(),
                                                     origValue, salesLine.CurrencyCode,
                                                     _mcrContinuityCustLine.Price);
    }
    else
    {
        MCROverrideLogin_OverridePrice mcrOverrideLogin_OverridePrice = new MCROverrideLogin_OverridePrice();
        retailInformationSubcodeIdLocal = mcrOverrideLogin_OverridePrice.launchPriceOverrideReasonCode(_mcrContinuityCustLine, NoYes::Yes);
        if (retailInformationSubcodeIdLocal)
        {
            element.linePriceOverrideYes(retailInformationSubcodeIdLocal, curUserId());
            mcrOrderEventTable.insertFromContinuityCustLine(mcrContinuityCustHeader.SalesId,
                                                             mcrLinePriceOverrideEventType,
                                                             curUserId(),
                                                             origValue, salesLine.CurrencyCode,
                                                             _mcrContinuityCustLine.Price);
        }
        else
        {
            cancelOverride = true;
        }

        // If user clicks cancel on the override form restore old values.
        if (cancelOverride)
        {
            _mcrContinuityCustLine.(_fieldId) = origValue;
            _mcrContinuityCustLine.Price = origValue;
            element.linePriceOverrideNo(curUserId());
        }
    }
}

