/// <summary>
/// Updates and initializes values when the field value is changed.
/// </summary>
/// <remarks>
/// When user modifies the price on the continuity cust line, need to prompt for reason code.
/// pass in the original price value in case user decides to cancel out.
/// </remarks>
public void modified()
{
    MCRContinuityCustLine   continuityCustLine;
    SalesPrice              originalValue;

    continuityCustLine = MCRContinuityCustLine::findLineId(mcrContinuityCustLine.LineID, false);

    originalValue = continuityCustLine.Price;

    mcrContinuityCustLine_ds.mcrEditPrice(true, continuityCustLine, mcrContinuityCustLine.Price);

    // If the new value was accepted, then refresh the data source else reread
    // the buffer.
    if (continuityCustLine.Price != originalValue)
    {
        mcrContinuityCustLine_ds.refresh();
        mcrContinuityCustLine_PriceOverride.allowEdit(true);
    }
    else
    {
        mcrContinuityCustLine_PriceOverride.allowEdit(false);
        mcrContinuityCustLine_ds.reread();
    }
}

/// <summary>
/// Updates and initializes values when the field value is changed.
/// </summary>
/// <remarks>
/// If the PriceOverride is unchecked when checked programmatically, revert the value of the
/// Price field to be what was set up on the schedule.
/// </remarks>
public void modified()
{
    MCRContinuityScheduleLine   continuityScheduleLine;
    SalesLine                   salesLine_Curr;

    super();
    continuityScheduleLine = MCRContinuityScheduleLine::findSchedIdEventId(mcrContinuityCustHeader.ScheduleId,
                                                                            mcrContinuityCustLine.EventId,
                                                                            false);
    if (mcrContinuityCustLine_PriceOverride.value() == NoYes::No)
    {
        // User has chosen to revert to original value.
        mcrContinuityCustLine.Price = continuityScheduleLine.NewPrice;
        // Convert the price if needed to current currency.
        salesLine_Curr = SalesLine::findInventTransId(mcrContinuityCustLine.InventTransId);
        mcrContinuityCustLine.Price = continuityScheduleLine.convertPrice(salesLine_Curr.CurrencyCode);
        mcrContinuityCustLine_PriceOverride.value(NoYes::No);
        mcrContinuityCustLine_ds.refresh();
    }
}

public void initValue()
{
    SalesTable  salesTable;

    mcrCustPaymTable.RefTableId = mcrContinuityCustHeader.TableId;
    mcrCustPaymTable.RefRecId = mcrContinuityCustHeader.RecId;

    super();

    salesTable = SalesTable::find(mcrContinuityCustHeader.SalesId, false);
    mcrCustPaymTable.CustAccount = salesTable.InvoiceAccount;
    mcrCustpaymTable.CurrencyCode = salesTable.CurrencyCode;
}

/// <summary>
/// Updates controls based upon the results of the query.
/// </summary>
public void executeQuery()
{
    boolean     allowEdit;

    super();

    if (MCRCustPaymTable::count(mcrContinuityCustHeader.TableId,
                              mcrContinuityCustHeader.RecId) >= 1)
    {
        allowEdit = false;
    }
    else
    {
        allowEdit = true;
    }

    mcrCustPaymTable_ds.allowEdit(allowEdit);
    mcrCustPaymTable_ds.allowCreate(allowEdit);

    if (!mcrCustPaymTable)
    {
        // Necessary to "allow create" in order to successfully create a new record.
        mcrCustPaymTable_ds.allowCreate(true);

        mcrCustPaymTable_ds.create(false);

        // Necessary to not "allow create" to prevent the user from
        // creating yet another record while within payment fields.
        mcrCustPaymTable_ds.allowCreate(false);
    }

    mcrCustPaymTable_ds.reread();

    // Update properties of payment controls.
    element.setPaymentControls();
}

