/// <summary>
/// Overrides the look up method to only look up the valid schedules
/// that are attached to the continuity scheduleid.
/// </summary>
public void lookup()
{
    if (mcrContinuityCustHeader.BillUpFront == true
        && MCRContinuityScheduleHeader::find(mcrContinuityCustHeader.ScheduleId).PromptForPaymSched == true)
    {
        MCRValidPaymentSchedule::lookupValidPaymScheds(this, mcrContinuityCustHeader.ScheduleId);
    }
    else
    {
        super();
    }
}

public boolean modified()
{
    boolean ret;
    // Lookup modified current record, refresh datasource
    //      to get most current.
    mcrCustPaymTable_ds.refresh();

    ret = super();

    return ret;
}

public void lookup()
{
    CreditCardCust::lookupCreditCard(this,
                                    mcrCustPaymTable.CustAccount,
                                    mcrCustPaymTable.PaymInfoRecId);
}

void clicked()
{
    element.addNewCardHolder();
}
