/// <summary>
/// Sets any passed parameters in case called from another form.
/// </summary>
public void init()
{
    Common      common = element.args().record();
    CustTable   custTable;
    SalesTable  salesTable;
    super();

    // Select the mode in which to start the form, based on the arguments passed.
    if (common)
    {
        switch (common.TableId)
        {
            case tableNum(SalesTable):
                salesTable = common;
                SalesLine_CustAccount.text(salesTable.CustAccount);
                break;
            case tableNum(CustTable):
                custTable = common;
                SalesLine_CustAccount.text(custTable.AccountNum);
                break;
        }
    }
}

        public void executeQuery()
{
    super();

    // Load the payments based on sales ID.
    MCRCustPaymTable_ds.executeQuery(SalesLine.SalesId);
}

        public void init()
{
    super();

    MCRCustPaymTable_ds.cacheAddMethod(tableMethodStr(MCRCustPaymTable, displayOrderId), false);
}

/// <summary>
/// Gets the payments depending on the continuity order type.
/// </summary>
/// <param name="_strSalesID">
/// The <c>SalesId</c> value to use for building the query if one is passed; optional.
/// </param>
/// <remarks>
/// Bill up front type is attached only to parent order.
/// Bill up front with schedule type is attached to parent order payment schedules.
/// Child Payment schedule type is tied to payment schedule on child order.
/// Child, no payment schedule type has normal order payments attached.
/// </remarks>
public void executeQuery(SalesID _strSalesID = "")
{
    Query                 query;
    QueryBuildDataSource  queryBuildDatasource;
    QueryBuildDataSource  qbdsSalesLink;
    QueryBuildDataSource  queryBuildDatasourceSalesTable;

    SalesTable          salesTable;

    // Load the payments based on the continuity parent type.
    if (_strSalesID)
    {
        salesTable = SalesTable::find(_strSalesID);

        // Setup a new query.
        query = new query();
        queryBuildDatasource = query.AddDataSource(tableNum(MCRCustPaymTable));
        queryBuildDatasource.addRange(fieldnum(MCRCustPaymTable, Amount)).value('!0');

        // If bill up front, it is attached to order or payment schedule.
        if (MCRContinuityCustHeader.BillUpFront)
        {
            if (MCRContinuityCustHeader.PaymentScheduleId)
            {
                queryBuildDatasource.addRange(fieldnum(MCRCustPaymTable, RefTableID)).value(int2str(tableNum(CustPaymSchedLine)));
            }
            else
            {
                queryBuildDatasource.addRange(fieldnum(MCRCustPaymTable, RefTableId)).value(SysQuery::value(salesTable.TableId));
                queryBuildDatasource.addRange(fieldnum(MCRCustPaymTable, RefRecId)).value(SysQuery::value(salesTable.RecId));
            }
        }
        else
        {
            // Payments are on the child records.
            queryBuildDatasourceSalesTable = queryBuildDatasource.addDataSource(tableNum(SalesTable));
            queryBuildDatasourceSalesTable.addRange(fieldnum(SalesTable, SalesId)).value(_strSalesID);
            queryBuildDatasourceSalesTable.relations(true);
            queryBuildDatasourceSalesTable.joinMode(JoinMode::InnerJoin);

            qbdsSalesLink =  queryBuildDatasourceSalesTable.addDataSource(tableNum(SalesTableLinks));
            queryBuildDatasourceSalesTable.relations(true);
            qbdsSalesLink.joinMode(joinmode::InnerJoin);
            qbdsSalesLink.addRange(fieldnum(SalesTableLinks, MCRMainInventTransID)).value(MCRContinuityCustHeader.InventTransId);
        }

        // Load the payment schedule as well.
        CustPaymSchedLine_ds.executeQuery(MCRContinuityCustHeader);

        // Load this query.
        this.query(query);
        super();
    }
}

/// <summary>
/// Loads the continuity sales order lines for the customer entered.
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource  qbdsSalesLine, qbdsMCRSalesLine;
    CustAccount           strCustAccount;

    // Gets the customer from the field on the form.
    strCustAccount = SalesLine_CustAccount.text();

    // Only load the grid if a customer is specified.
    if (strCustAccount)
    {
        qbdsSalesLine = this.query().dataSourceTable(tableNum(SalesLine));
        qbdsSalesLine.clearRanges();
        qbdsSalesLine.addRange(fieldnum(SalesLine, CustAccount)).value(strCustAccount);
        qbdsMCRSalesLine = qbdsSalesLine.addDataSource(tableNum(MCRSalesLine));
        qbdsMCRSalesLine.relations(true);
        qbdsMCRSalesLine.joinMode(JoinMode::InnerJoin);

        qbdsMCRSalesLine.addRange(fieldnum(MCRSalesLine, ContinuityScheduleID)).value(SysQuery::valueNotEmptyString());
        qbdsSalesLine.addSortField(fieldNum(SalesLine, SalesId), SortOrder::Descending);

        super();
    }
}

/// <summary>
/// Gets the payment schedules not billed based on the continuity order type.
/// </summary>
/// <param name="_MCRContinuityCustHeader">
/// The <c>MCRContinuityCustHeader</c> record to use for building the query; optional.
/// </param>
/// <remarks>
/// Bill up front with schedule type is attached to the parent order payment schedules.
/// Child Payment schedule type is tied to a payment schedule on the child order.
/// </remarks>
public void executeQuery(MCRContinuityCustHeader _MCRContinuityCustHeader = null)
{
    Query                 query;
    QueryBuildDataSource  qbdsMain;
    QueryBuildDataSource  qbdsCustPaymSched;
    QueryBuildDataSource  qbdsContCustLine;
    QueryBuildDataSource  qbdsSalesTable;

    // Load the payment schedules if passed a Continuity Cust Header.
    if (_MCRContinuityCustHeader)
    {
        // Setup a new query.
        query = new query();
        qbdsMain = query.AddDataSource(tableNum(CustPaymSchedLine));
        qbdsCustPaymSched =  qbdsMain.addDataSource(tableNum(CustPaymSched));
        qbdsCustPaymSched.joinMode(joinmode::InnerJoin);
        qbdsCustPaymSched.addLink(fieldnum(CustPaymSchedLine, ParentRecID), fieldnum(CustPaymSched, RecID));

        // If bill up front, it is attached to order or payment schedule.
        if (_MCRContinuityCustHeader.BillUpFront)
        {
            // CustPaymSched is attached to the continuity cust header.
            qbdsCustPaymSched.addRange(fieldnum(CustPaymSched, ExtTableID)).value(int2str(_MCRContinuityCustHeader.TableId));
            qbdsCustPaymSched.addRange(fieldnum(CustPaymSched, ExtRecID)).value(int642str(_MCRContinuityCustHeader.RecId));
        }
        else
        {
            // CustPaymSched is attached to the sales order header.
            qbdsCustPaymSched.addRange(fieldnum(CustPaymSched, ExtTableID)).value(int2str(tableNum(SalesTable)));
            qbdsSalesTable =  qbdsCustPaymSched.addDataSource(tableNum(SalesTable));
            qbdsSalesTable.joinMode(joinmode::InnerJoin);
            qbdsSalesTable.addLink(fieldnum(CustPaymSched, ExtRecID), fieldnum(SalesTable, RecID));

            // Add in the continuity cust line for child orders.
            qbdsContCustLine =  qbdsSalesTable.addDataSource(tableNum(MCRContinuityCustLine));
            qbdsContCustLine.joinMode(joinmode::InnerJoin);
            qbdsContCustLine.addLink(fieldnum(SalesTable, SalesID), fieldnum(MCRContinuityCustLine, SalesID));
            qbdsContCustLine.addRange(fieldnum(MCRContinuityCustLine, InventTransID)).value(_MCRContinuityCustHeader.InventTransId);
        }

        this.query(query);
        super();
    }
}

/// <summary>
/// Processes a customer entered by loading continuity lines for this customer.
/// </summary>
/// <returns>
/// true if the form control value was modified successfully; otherwise, false.
/// </returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    //Execute the query with the customer.
    SalesLine_ds.executeQuery();

    return ret;
}
