[Form]
/// <summary>
/// The <c>MCRContinuitySchedule</c> allows the user to view and modify
/// continuity schedules.
/// </summary>
public class MCRContinuitySchedule extends FormRun
{
    InventDimCtrl_Frm   inventDimFormSetup;
}

/// <summary>
/// Checks if the schedule has at least one event line that has the action code
/// of end.
/// </summary>
/// <returns>
/// true if the schedule has at least one event line has the action code of end;
/// otherwise, false.
/// </returns>
public boolean canClose()
{
    boolean                     ret = true;

    // Schedule set up form can close only if the MCRContinuityCustLine has
    // one record with an action code of end.
    ret = MCRContinuityScheduleHeader::validateContinuitySchedule();

    if (ret)
    {
        ret = super();
    }
    return ret;
}

/// <summary>
/// Sets the visibility of the payment schedule tab based on the
/// prompt for schedule flag on the header.
/// </summary>
void setHeaderControls()
{
    if (MCRContinuityScheduleHeader.BillUpfront)
    {
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, PaymSchedId)).enabled(true);
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, PromptForPaymSched)).enabled(true);
        Schedules.visible(MCRContinuityScheduleHeader.PromptForPaymSched);
    }
    else
    {
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, PaymSchedId)).enabled(false);
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, PromptForPaymSched)).enabled(false);
        Schedules.visible(false);
    }

    // Enabled field only if schedule is first order.
    if (MCRContinuityScheduleHeader.OrderStart == MCRContinuityOrderStart::FirstEvent)
    {
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, ConsolidatePastOrders)).enabled(true);
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, ConsolidatePastOrders)).skip(false);
    }
    else
    {
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, ConsolidatePastOrders)).enabled(false);
        MCRContinuityScheduleHeader_ds.object(fieldNum(MCRContinuityScheduleHeader, ConsolidatePastOrders)).skip(true);
        // Need to remove consolidation if selection changes.
        MCRContinuityScheduleHeader.consolidatePastOrders = NoYes::No;
    }
}

/// <summary>
/// Enables and disables parts of the continuity schedule line as needed.
/// </summary>
void setLineControls()
{
    boolean     priceEnabled;

    priceEnabled = (!mcrContinuityScheduleHeader.BillUpfront
                    && mcrContinuityScheduleLine.ItemID);
    MCRContinuityScheduleLine_Price.enabled(priceEnabled);

    if (MCRContinuityScheduleLine.ItemID)
    {
        MCRContinuityScheduleLine_ds.object(fieldNum(MCRContinuityScheduleLine, ItemId)).allowEdit(false);
        InventDimGroupSetup::newItemId(mcrContinuityScheduleLine.ItemId).inventDimParmActive(inventDimFormSetup.parmDimParmEnabled());
        inventDimFormSetup.formSetControls(false);
    }
    else
    {
        MCRContinuityScheduleLine_ds.object(fieldNum(MCRContinuityScheduleLine, ItemId)).allowEdit(true);
    }

    // Don't allow the line Payment Schedules to change if Bill Up Front is set.
    MCRContinuityScheduleLine_ds.object(FieldNum(MCRContinuityScheduleLine, PaymSchedId)).allowEdit(!MCRContinuityScheduleHeader.BillUpfront);
}

public void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm dimParmLockedRightClick;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:

            if (!inventDimFormSetup)
                {
                    inventDimFormSetup = InventDimCtrl::newFromFormValidDataSource(element, InventDim_ds.name()) as InventDimCtrl_Frm;
                }
                dimParmLockedRightClick.setAllProductDimensions();
                dimParmLockedRightClick.InventSiteIdFlag = true;
                dimParmLockedRightClick.InventLocationIdFlag = true;
                dimParmLockedRightClick.toggleAllInventDim();
                inventDimFormSetup.parmDimParmLockedRightClick(dimParmLockedRightClick);
                inventDimFormSetup.formSetControls(true);

            break;

        case InventDimFormDesignUpdate::Active:
        case InventDimFormDesignUpdate::FieldChange:
            InventDimGroupSetup::newItemId(mcrContinuityScheduleLine.ItemId).inventDimParmActive(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls(false);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public void init()
{
    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// Validates that if the record is set to prompt for payment schedule
/// it is also bill upfront.
/// </summary>
/// <returns>
/// true if the record is set to prompt for payment schedule and is also bill upfront;
/// otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean ret = true;

    if (mcrContinuityScheduleHeader.PromptForPaymSched == true
        && mcrContinuityScheduleHeader.BillUpfront == false)
    {
        ret = false;
        throw error("@MCR12883");
    }

    if (ret)
    {
        ret = super();
    }

    return ret;
}

/// <summary>
/// Initialzes the active flag to yes when a new schedule is created.
/// </summary>
/// <param name="_append">
/// A <c>Boolean</c> value that defines whether the schedule should be appended or not; optional.
/// </param>
public void create(boolean _append = false)
{
    super(_append);

    MCRContinuityScheduleHeader.Active = NoYes::No;
    MCRContinuityScheduleHeader.CurrentEvent = 1;
}

public int active()
{
    int ret;

    // Initialize the header controls.
    element.setHeaderControls();

    ret = super();
    return ret;
}

/// <summary>
/// Ensures that the user does not inactivate a schedule that has already
/// been tied to an item.
/// </summary>
/// <returns>
/// true if the schedule has not been tied to an item; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret
        && !mcrContinuityScheduleHeader.Active)
    {
        if (InventTable::mcrFindByContinuitySchedId(MCRContinuityScheduleHeader.ScheduleID, false))
        {
            ret = false;
            throw error("@MCR12135");
        }
        else
        {
            ret = true;
        }
    }
    return ret;
}

public void modified()
{
    super();
    element.setHeaderControls();
}

/// <summary>
/// Warns the user if conslidate is checked.
/// </summary>
public void modified()
{
    super();

    // If consolidate is checked warn user about how
    // sales order level values are assigned during child creation.
    if (MCRContinuityScheduleHeader.consolidatePastOrders)
    {
        warning("@MCR35751");
    }
}

public void modified()
{
    element.setHeaderControls();
    super();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (mcrContinuityScheduleHeader.BillUpfront
        && mcrContinuityScheduleHeader.PromptForPaymSched)
    {
        MCRValidPaymentSchedule::lookupValidPaymScheds(_formControl, MCRContinuityScheduleHeader.ScheduleID);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

/// <summary>
/// Shows or hides the payment schedules tab based on the prompt for payment schedule being
/// checked or unchecked.
/// </summary>
public void modified()
{
    super();

    if (MCRContinuityScheduleHeader.PromptForPaymSched == NoYes::Yes)
    {
        Schedules.visible(true);
        if (MCRContinuityScheduleHeader.BillUpfront != true)
        {
            throw warning ("@MCR12883");
        }
    }
    else
    {
        Schedules.visible(false);
        if (mcrValidPaymentSchedule::validPaymSchedsExist(MCRContinuityScheduleHeader.ScheduleID))
        {
            throw warning("@MCR12751");
        }
    }
}

public boolean validateWrite()
{
    boolean ret;

    MCRContinuityScheduleLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    return ret;
}

/// <summary>
/// Sorts by <c>EventId </c> when reloading the grid.
/// </summary>
public void executeQuery()
{
    super();

    this.query().dataSourceTable(tableNum(MCRContinuityScheduleLine)).addSortField(fieldNum(MCRContinuityScheduleLine, EventId), SortOrder::Ascending);
}

/// <summary>
/// Ensures the other lines with this event are changed and updated.
/// </summary>
public void write()
{
    MCRContinuityScheduleLine    mcrContinuityScheduleLineLocal;

    super();

    mcrContinuityScheduleLineLocal = MCRContinuityScheduleLine.data();
    MCRContinuityScheduleLine.updateOtherLines();
    MCRContinuityScheduleLine_ds.research();
    element.args().lookupRecord(mcrContinuityScheduleLineLocal);
    MCRContinuityScheduleLine_ds.research(false);

    if (MCRContinuityScheduleLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(MCRContinuityScheduleLine.InventDimId));
        inventDim_ds.setCurrent();
    }
}

/// <summary>
/// Adds the line number and <c>ScheduleId</c> to the line.
/// </summary>
/// <param name="_append">
/// A <c>Boolean</c> value that defines whether the schedule should be appended or not; optional.
/// </param>
public void create(boolean _append = false)
{
    MCRContinuityScheduleLine   mcrContinuityScheduleLineLocal;
    MCRContinuityEventId        mcrContinuityEventId;

    super(_append);

    select firstOnly mcrContinuityScheduleLineLocal
        order by lineNum desc
        where mcrContinuityScheduleLineLocal.ScheduleID == MCRContinuityScheduleHeader.ScheduleID;

    MCRContinuityScheduleLine.LineNum = trunc(mcrContinuityScheduleLineLocal.LineNum) + 1;

    // Default the appropriate event id. Default the event id to be the current event
    // the user can choose to change it to an event id greater than the current event.
    mcrContinuityEventId = MCRContinuityScheduleLine::retHighestEventId(MCRContinuityScheduleHeader.ScheduleID) + 1;
    if (!mcrContinuityEventId)
    {
        MCRContinuityScheduleLine.EventID = MCRContinuityScheduleHeader::retCurrentEvent(MCRContinuityScheduleHeader.ScheduleID);
    }
    else
    {
        MCRContinuityScheduleLine.EventID = mcrContinuityEventId;
    }

}

public int active()
{
    int ret;

    ret = super();

    //Sets line controls.
    element.setLineControls();
    element.updateDesign(InventDimFormDesignUpdate::Active);
    return ret;
}

/// <summary>
/// Ensures the <c>ItemID</c> value cannot be changed once it has been entered.
/// </summary>
public void modified()
{
    super();
    element.setLineControls();
}

public void write()
{
    //Skip call to super, parent table will handle inventdim operations.
}

void clicked()
{
    common  common = MCRContinuityScheduleHeader.data();

    super();

    MCRContinuityScheduleHeader_ds.executeQuery();
    MCRContinuityScheduleHeader_ds.positionToRecord(common);
}

/// <summary>
/// Passes the selected continuity <c>ScheduleId</c> value.
/// </summary>
void clicked()
{
    Args            args;
    FormRun         formRun;

    args = new Args();

    if (MCRContinuityScheduleHeader)
    {
        args.parm(MCRContinuityScheduleHeader.ScheduleID);
    }

    formRun = new MenuFunction(menuitemdisplaystr(MCRContinuityItemChange), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();
}
