[Form]
/// <summary>
///     The <c>MCRCustUnmergeWorkbench</c> form contains a grid of all merged customers, with the ability
///     to unmerge them, as well as view a tree structure of the merged customer's parents and children.
/// </summary>
public class MCRCustUnmergeWorkbench extends FormRun
{
}

/// <summary>
///     Adds the children to the tree.
/// </summary>
/// <param name="accountNum">
/// The customer account number of the merged parent customer record to add to the tree.
/// </param>
/// <param name="currentPlace">
/// Indicates where in the tree to add the node.
/// </param>
void addChildrenToTree(CustAccount accountNum, int currentPlace)
{
    CustTable    custTableLocal;
    int          childPlace;

    while select custTableLocal where custTableLocal.MCRMergedParent == accountNum
    {
        childPlace = SysFormTreeControl::addTreeItem(mergeTree, strfmt("%1 - %2", custTableLocal.AccountNum, custTableLocal.Name()), currentPlace, strfmt("%1 - %2", custTableLocal.AccountNum, custTableLocal.Name()));

        // recursive call
        element.addChildrenToTree(custTableLocal.AccountNum, childPlace);
    }
}

/// <summary>
///     Adds to the tree the current customer building the tree from.
/// </summary>
/// <param name="_currentPlace">
/// Indicates where in the tree to add the node.
/// </param>
/// <returns>
/// The location of the node that was added.
/// </returns>
int addMergedCustomerToTree(int _currentPlace)
{
    formTreeItem item;
    int currentPlace = _currentPlace;

    currentPlace = SysFormTreeControl::addTreeItem(mergeTree, strfmt("%1 - %2", CustTable.AccountNum, CustTable.Name()), currentPlace, strfmt("%1 - %2", CustTable.AccountNum, CustTable.Name()));

    // bold item
    item = mergeTree.getItem(currentPlace);
    item.stateBold(true);
    mergeTree.setItem(item);

    return currentPlace;
}

/// <summary>
///     Adds the parents of the currently selected merged customer to the merge tree.
/// </summary>
/// <returns>
/// The current place in tree to add the next set of items.
/// </returns>
int addParentsToTree()
{
    Container listOfParents;
    CustTable custTableLocal;
    int containerPos;
    int lastPlace;

    str 50 tempStr;

    // add immediate parent of merged customer first to container.
    custTableLocal = CustTable::find(CustTable.MCRMergedParent);
    listOfParents += strfmt("%1 - %2", custTableLocal.AccountNum, custTableLocal.Name());

    // add any other possible parents to the container (all the way to the root of the merge)
    while (custTableLocal.MCRMergedParent)
    {
        custTableLocal = CustTable::find(custTableLocal.MCRMergedParent);
        listofParents += strfmt("%1 - %2", custTableLocal.AccountNum, custTableLocal.Name());
    }

    // now reverese through the container adding the parents found to the merge tree.
    containerPos = conlen(listofParents);
    while (containerPos > 0)
    {
        tempStr = conpeek(listofparents, containerPos);

        lastPlace = SysFormTreeControl::addTreeItem(mergeTree, conpeek(listOfParents, containerPos), lastPlace, conpeek(listOfParents, containerPos));

        containerPos--;
    }

    // returns current place in tree, to add the next set of items
    return lastPlace;
}

/// <summary>
///     Creates merge tree for currently selected merged customers.
/// </summary>
/// <remarks>
/// Creates tree by first adding parents to the merged customers, then adding
///     the merged customer, and finally adding any potential children that were previously
///     merged into the merged customer.
/// </remarks>
void drawTree()
{
    int currentPlace;

    MergeTree.deleteAll();
    currentPlace = element.addParentsToTree();
    currentPlace = element.addMergedCustomerToTree(currentPlace);
    element.addChildrenToTree(CustTable.AccountNum, currentPlace);

    // fully expands the tree, otherwise would start out collapsed.
    sysFormTreeControl::expandTree(MergeTree, MergeTree.getRoot());
}

/// <summary>
///     Redraws the tree and updates the unmerge button when selecting a merged customer.
/// </summary>
/// <returns>
/// The currently selected record index.
/// </returns>
public int active()
{
    int ret;

    ret = super();

    // draw the tree and select the first node (this is so that whenever merged customers are seleced, correct details
    // are displayed to the right of the merge tree (instead of old data from the previous query).
    element.drawTree();
    mergeTree.select(MergeTree.getRoot());

    // updates the button's text to include the account number and name of the currently selected merged customer.
    // this is to clear up any possible confusions on what account is being unmerged
    UnmergeButton.enabled(true);
    UnMergeButton.text(strfmt("@MCR12286", CustTable.AccountNum, CustTable.Name()));

    return ret;
}

/// <summary>
///     Displays only the customers who have been merged.
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;

    // a customer who has a custAccount in the MCRMergedParent or MCRMergedRoot
    // fields has been merged.
    queryBuildDataSource = this.queryBuildDataSource();
    queryBuildDataSource.addRange(FieldNum(CustTable, MCRMergedParent)).value(sysQuery::valueNotEmptyString());

    super();
}

/// <summary>
///     Removes initialized range if merged customers were found.
/// </summary>
public void executeQuery()
{
    if (CustTable)
    {
        this.queryBuildDataSource().clearRanges();
    }

    super();
}

/// <summary>
///     Initializes the datasource to have no records, in case there are no merged customers
///     in the system, this datasource should be empty.
/// </summary>
public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    // a customer who has a custAccount in the MCRMergedParent or MCRMergedRoot
    // fields has been merged.
    queryBuildDataSource = this.queryBuildDataSource();
    queryBuildDataSource.addRange(FieldNum(CustTable, AccountNum)).value(sysQuery::valueEmptyString());

    this.cacheAddMethod(tableMethodStr(CustTable, Name));
    this.cacheAddMethod(tableMethodStr(CustTable, Address));
}

/// <summary>
///     Calls logic to unmerge then refreshes the grid of merged customers.
/// </summary>
void clicked()
{
    DialogButton dialogButton;

    super();

    // provide warning of operation
    dialogButton = box::okCancel(strfmt("@MCR12297", CustTable.AccountNum, CustTable.MCRMergedParent), DialogButton::Ok, "@SYS866");
    if (dialogButton == dialogButton::Cancel)
    {
        return;
    }

    MCRCustomerMerge::unmergeCustomer(CustTable.AccountNum);

    MergeTree.deleteAll();
    CustTable_ds.executeQuery();
}

/// <summary>
///     Updates the details when an item in the merge tree is seleced.
/// </summary>
/// <param name="_OldItem">
///     The item that used to be selected.
/// </param>
/// <param name="_NewItem">
/// The item that is now currently selected.
/// </param>
/// <param name="_how">
/// Indicates how the node was selected.
/// </param>
public void selectionChanged(FormTreeItem _OldItem, FormTreeItem _NewItem, FormTreeSelect _how)
{
    str data;
    CustAccount accountNum;
    container c;

    super(_OldItem, _NewItem, _how);

    // grab out the data from the tree
    data = _newItem.data();

    // parse out the account number (Data is in the format of 'Account_Number - Customer_Name', so can parse by space
    c = str2con(data, ' ');
    accountNum = conPeek(c, 1);

    select CustTable_Details where CustTable_Details.AccountNum == accountNum;
    CustTable_Details_ds.reread();
    InfoGroup.setFocus();
}
