/// <summary>
/// Changes the customer on the form.
/// </summary>
/// <param name="_custAccount">
/// The customer account to change to on the form.
/// </param>
void ChangeCust(CustAccount _custAccount)
{
    this.chosenSalesIdList = null;
    this.chosenCustomer = _custAccount;
    custTable_ds.executeQuery();
    this.changeMode(currentMode);
}

public void changeMode(int _newMode)
{
    if (_newMode > 0 && _newMode < 4)
    {
        if (showDateFilters)
        {
            boolean tabDateFiltersVisible = _newMode == #SalesOrder || _newMode == #InvoiceOrder;
            TabDateFilters.visible(tabDateFiltersVisible);
        }

        // Because the SalesTable data source link type is Delayed, the form engine will call it automatically when the customer changes.
        // In these cases, there is no need to update the order view or call executeQuery, since that will degrade performance.
        // This is not the case for MCRHistSalesTable (#ArchivedOrder) or InvoiceSalesTable (#InvoiceOrder), where the link type is Passive.
        if (!RetailOptimizeSalesTableExecuteQueryCustomerServiceFormToggle::instance().isEnabled()
            || currentMode != _newMode
            || currentMode != #SalesOrder)
        {
            currentMode = _newMode;
            this.updateOrderView();
        }
    }
}

private void selectSalesOrder(List _salesId, int _listIndex)
{
    SalesId salesID;
    container con;

    con = list2Con(_salesId);
    this.chosenSalesIdList = _salesId;
    salesID = conPeek(con, _listIndex);
    this.chosenCustomer = SalesTable::find(salesID).CustAccount;
    custTable_ds.executeQuery();

    this.changeMode(currentMode);
}

/// <summary>
/// Closes the form.
/// </summary>
public void close()
{
    // Saves the user's customer search type.
    MCRCustSearchType mcrCustSearchType;
    mcrCustSearchType = str2Enum(mcrCustSearchType, CustSearchType.valueStr());
    MCRCustSearch::saveCustSearchType(mcrCustSearchType);

    MCROrderSearchType mcrOrderSearchType;
    mcrOrderSearchType = str2Enum(mcrOrderSearchType, OrderSearchType.valueStr());
    MCROrderSearch::saveOrderSearchType(mcrOrderSearchType);

    super();
}

/// <summary>
/// Initializes the form values.
/// </summary>
void init()
{
    CustTable   custTableLocal;
    SalesTable  salesTableLocal;
    boolean     isCustSearch = isConfigurationkeyEnabled(configurationKeyNum(TradeCustomerSearch));
    str currentUrl = UrlUtility::getUrl();

    super();

    mcrOrderParameter = MCROrderParameters::find();

    if (element.args().caller() && element.args().dataset() == tableNum(CustTable))
    {
        custTableLocal = element.args().record();
        custTable_ds.query().dataSourceTable(tableNum(CustTable)).clearDynalinks();
        element.ChangeCust(custTableLocal.AccountNum);
        notesString.text(custTable_ds.mcrGetCustomerNotes());
    }
    // Called from salestableListPage form.
    else if (element.args().caller() && element.args().dataset() == tableNum(SalesTable))
    {
        salesTableLocal = element.args().record() as SalesTable;
        custTableLocal = CustTable::find(salesTableLocal.CustAccount);
        custTable_ds.query().dataSourceTable(tableNum(CustTable)).clearDynalinks();
        element.ChangeCust(custTableLocal.AccountNum);
        notesString.text(custTable_ds.mcrGetCustomerNotes());
    }
    // Check for currentURL for customer.
    else if (!System.String::IsNullOrEmpty(currentUrl))
    {
        System.Uri pageUri = new System.Uri(currentUrl);
        System.Collections.Specialized.NameValueCollection queryParams = System.Web.HttpUtility::ParseQueryString(pageUri.Query);
        str customerId = queryParams.Get("customerId");

        // CustomerId is specified and recognized, so select it to be loaded in the page.
        if (!System.String::IsNullOrEmpty(customerId) && CustTable::exist(customerId))
        {
            custTable_ds.query().dataSourceTable(tableNum(CustTable)).clearDynalinks();
            element.ChangeCust(customerId);
            notesString.text(custTable_ds.mcrGetCustomerNotes());
        }
    }

    CustomerSearch.visible(isCustSearch);
    CustAccountGroup.visible(!isCustSearch);
    MCRPriceHistory.visible(CustParameters::find().MCREnablePriceDetails);

    element.updateDesign(InventDimFormDesignUpdate::Init);
    switch (currentMode)
    {
        case #InvoiceOrder:
            invoiceOrderLines.visibleColsValue(8 + conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));
            break;

        case #ArchivedOrder:
            mcrHistSalesLineOrderLines.visibleColsValue(8 + conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));
            break;

        default:
            orderLines.visibleColsValue(8 + conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));
            break;
    }

    orderLines.visible(true);
    mcrHistSalesLineOrderLines.visible(true);

    imagelist = new Imagelist(Imagelist::iconWidth(), Imagelist::smallIconHeight());
    shipToIcon_SO.imageList(imagelist);

    onInit = true;
    map = new Map(Types::Integer, Types::String);

    element.orderSearchFeatureManagement();

    if (shouldLimitSalesTableColumns)
    {
        SOCreateReturn.needsRecord(true);
        ModifyOrder.needsRecord(true);
        TaxTransSource.needsRecord(true);
        ContinuityData.needsRecord(true);

        FormFunctionButtonControl localSalesOrderCaseButton = element.design().controlName(formControlStr(MCRCustomerService, SalesOrderCaseButton));
        FormFunctionButtonControl localOrderNotes = element.design().controlName(formControlStr(MCRCustomerService, OrderNotes));

        localSalesOrderCaseButton.needsRecord(true);
        localOrderNotes.needsRecord(true);

        MoreOptions.enabled(false);
    }

    showDateFilters = mcrOrderParameter.ShowDateFilterInCustomerService;

    if (showDateFilters)
    {
        date currentUserDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
        MCRDefaultLookbackWindowCustomerService lookbackWindow = mcrOrderParameter.DefaultLookbackWindowCustomerService;

        date toDate = currentUserDate;
        date fromDate = toDate - lookbackWindow;

        FromDateFilter.dateValue(fromDate);
        ToDateFilter.dateValue(toDate);

        TabDateFilters.visible(true);
    }
}

