/// <summary>
/// Calls customer search form and sets the customer.
/// </summary>
void mcrRunCustomerSearch()
{
    AccountNum      custSearchAccount;

    // If there is a search string.
    if (SearchText.valueStr())
    {
        MCRCustSearchType mcrCustSearchType;
        mcrCustSearchType = str2Enum(mcrCustSearchType, CustSearchType.valueStr());
        custSearchAccount = MCRCustSearch::searchDisplayForm(SearchText.text(), mcrCustSearchType, true, element);

        // If a customer was found.
        if (custSearchAccount != "")
        {
            // Change the customer.
            element.ChangeCust(custSearchAccount);
        }
    }
    notesString.text(custTable_ds.mcrGetCustomerNotes());
}

/// <summary>
/// Launches the order search form, where the user will select an order from the results.
/// Then sets the customer on the customer service form to the customer for the selected order.
/// </summary>
private void runOrderSearch()
{
    if (!OrderSearchText.valueStr())
    {
        return;
    }

    MCROrderSearchType mcrOrderSearchType;
    mcrOrderSearchType = str2Enum(mcrOrderSearchType, OrderSearchType.valueStr());

    List salesIdResult = MCROrderSearch::searchDisplayForm(OrderSearchText.text(), mcrOrderSearchType, element);

    if (salesIdResult)
    {
        element.selectSalesOrder(salesIdResult, 1);
    }

    notesString.text(custTable_ds.mcrGetCustomerNotes());
}

/// <summary>
/// Loads and validates the user's saved customer search type.
/// </summary>
public void run()
{
    super();

    // Start by viewing sales orders.
    this.changeMode(#SalesOrder);

    boolean useSavedSearchField = MCRFullTextParameters::find().SaveSearchType;

    // Get the user's saved customer search type.
    MCRCustSearchType mcrCustSearchType = MCRCustSearch::getCustSearchType();

    // If the parameter is set to load the last saved and
    // the saved search type is valid, load it.
    DictEnum custSearchEnumDict = new DictEnum(enumNum(MCRCustSearchType));
    if (useSavedSearchField && custSearchEnumDict.value2Symbol(enum2int(mcrCustSearchType)))
    {
        custSearchType.selection(enum2int(mcrCustSearchType));
    }
    else
    {
        custSearchType.selection(enum2int(MCRCustSearchType::DefaultCustSearch));
    }

    MCROrderSearchType mcrOrderSearchType = mcrOrderSearch::getOrderSearchType();
    DictEnum orderSearchEnumDict = new DictEnum(enumNum(MCROrderSearchType));
    if (useSavedSearchField && orderSearchEnumDict.value2Symbol(enum2int(mcrOrderSearchType)))
    {
        orderSearchType.selection(enum2int(mcrOrderSearchType));
    }
    else
    {
        orderSearchType.selection(enum2int(MCROrderSearchType::ReceiptId));
    }

    // Sets focus to the customer search field.
    SearchText.setFocus();
}

