public int task(int _taskId)
{
    #Task
    int ret;
    int i;

    // When the user hits enter on the customer search
    //  text field, set focus back to the customer search field.
    if (_taskid != #taskF5)
    {
        ret = super(_taskId);
    }
    else
    {
        // Refresh all of the data in case data has changed since you last refreshed the form.
        for (i = 0;  i < this.dataSourceCount();  i++)
        {
            this.dataSource(i).refresh();
        }
    }

    if (_taskId == #taskEnter && searchEntered)
    {
        searchEntered = false;
        SearchText.setFocus();
    }

    return ret;
}

