void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventMovement movement;
    switch (currentMode)
    {
        case #InvoiceOrder:
            movement = InventMovement::construct(invoiceSalesLine);
            break;

        case #ArchivedOrder:
            movement = InventMovement::construct(mcrHistSalesLine);
            break;

        default:
            movement = InventMovement::construct(salesLine);
            break;
    }

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_Mov_OrderLine::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

public void updateOrderView()
{
    ordersTab.tab(currentMode);

    tabPageSalesOrders.enabled(currentMode == #SalesOrder);
    tabPageInvoices.enabled(currentMode == #InvoiceOrder);
    tabPageArchived.enabled(currentMode == #ArchivedOrder);

    switch (currentMode)
    {
        case #SalesOrder:
            salesTable_ds.executeQuery();
            break;
        case #InvoiceOrder:
            invoiceSalesTable_ds.executeQuery();
            break;
        case #ArchivedOrder:
            mcrHistSalesTable_ds.executeQuery();
    }
}

