/// <summary>
/// Validate whether return order allowed for selected sales order or not.
/// </summary>
/// <param name="_salesId">
/// selected sales order Id.
/// </param>
/// <returns>
/// return true if return order allowed else return false.
/// </returns>
/// <remarks>
/// It will return true if any of sales line in sales order have pending quantity to return.
/// </remarks>
public boolean isReturnOrderAllowed(SalesId _salesId)

{
    SalesQty            totReturnedQty = 0;
    PdsCWInventQty      totReturnedCWQty = 0;
    SalesLine           localSalesLine;
    CustInvoiceTrans    custInvoiceTrans;

    if (_salesId == '')
    {
        return false;
    }

    while select SalesId, ItemId, InventTransId, ProjId, SalesUnit from localSalesLine
        join custInvoiceTrans
        where custInvoiceTrans.InventTransId == localSalesLine.InventTransId
        && localSalesLine.SalesId == _salesId
    {
        // Calculates the returned quantities for a sales order line.
        [totReturnedQty, totReturnedCWQty] = SalesLine::pdsRemainReturnLotQtyAllCreditNote(
                                                    localSalesLine.InventTransId,
                                                        localSalesLine.ProjId,
                                                        localSalesLine.SalesUnit);

        // Check invoiced qty for both normal and catch weight item type
        if ((totReturnedQty < localSalesLine.invoicedInTotal()) || (totReturnedCWQty < localSalesLine.pdsCWInvoicedInTotal()))
        {
            return true;
        }
    }

    return false;
}

