/// <summary>
/// Collects all customer notes into one string.
/// </summary>
/// <returns>
/// A <c>Notes</c> string containing the customer notes.
/// </returns>
public Notes mcrGetCustomerNotes()
{
    DocuRef                 docuRef;
    TextBuffer              custNotesTextBuffer = new TextBuffer();
    SelectableDataArea      refCompanyID = curext();
    TransDate               dateCreated;

    while select docuRef order by RecId desc
        where   docuRef.RefCompanyId == refCompanyID
            &&  docuRef.RefTableId == tableNum(CustTable)
            &&  docuRef.RefRecId == custTable.RecId
    {
        if (docuRef.Notes != "")
        {
            // Build the line using a STRFMT function instead of individual labels.
            // Apply time zone offset to docuref
            dateCreated = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(docuRef.CreatedDateTime, DateTimeUtil::getCompanyTimeZone()));
            custNotesTextBuffer.appendText(strFmt("@MCR31836", docuRef.CreatedBy,
                    date2StrUsr(dateCreated, DateFlags::FormatAll), docuRef.Notes));
            custNotesTextBuffer.appendText('\n');
        }
    }
    return custNotesTextBuffer.getText();
}

public int active()
{
    int ret;
    CustVendorBlocked blockLevel = CustTable::blocked(custTable.AccountNum);
    ret = super();

    // Pop up hot alerts for customer issues if they exist.
    element.mcrHotAlertMsg();

    // Check if the customer has a stop placed on them.
    if (blockLevel == CustVendorBlocked::Invoice)
    {
        warning(strFmt("@SYS18389", custTable.AccountNum, enum2str(blockLevel)));
    }
    else if (blockLevel == CustVendorBlocked::All)
    {
        warning(strFmt("@SYS18389", custTable.AccountNum, enum2str(blockLevel)));
    }

    element.setTitleText();

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource source;
    str searchString;

    // Get the query datasource.
    source = this.query().dataSourceTable(tableNum(CustTable));

    source.clearRange(fieldNum(CustTable, AccountNum));

    // Search only for the specified customer.
    if (chosenCustomer)
    {
        searchString = chosenCustomer;

        // Re-initalize chosen customer after utilizing.
        chosenCustomer = "";
    }
    else
    {
        searchString = "..";
    }
    source.addRange(fieldNum(CustTable, AccountNum)).value(searchString);
    super();

    // Update the notes based on the customer account selected.
    NotesString.text(this.mcrGetCustomerNotes());

    // Run the execute query method to ensure that the data is up to date
    // before running the updateCustControls() method called below.
    smmBusRelTable_ds.executeQuery();

    // Update the properties of the customer controls.
    element.updateCustControls();
}

public void init()
{
    Query query = new Query();

    super();
    query.addDataSource(tableNum(CustTable));
    this.query(query);

    // Caches the customer display methods.
    this.cacheAddMethod(tableMethodStr(CustTable, name));
    this.cacheAddMethod(tableMethodStr(CustTable, email));
    this.cacheAddMethod(tableMethodStr(CustTable, address));
    this.cacheAddMethod(tableMethodStr(CustTable, phone));
    this.cacheAddMethod(tableMethodStr(CustTable, phoneLocal));
}

/// <summary>
/// Refreshes the record when the form has been opened by another form
/// and the user has selected a different record in the calling form.
/// </summary>
public void linkActive()
{
    CustTable   custTableLocal;

    if (element.args().caller() && element.args().dataset() == tableNum(CustTable))
    {
        custTableLocal = element.args().record();
        custTable_ds.query().dataSourceName(custTable_ds.name()).clearDynalinks();

        // Set the chosenCustomer, used when coming from the Customer form to open
        // customer service for a chosen customer.
        chosenCustomer = custTableLocal.AccountNum;
    }

    super();
}

/// <summary>
/// Validates to ensure that a phantom customer account cannot be manually entered.
/// </summary>
/// <returns>
/// true if the customer account has not been merged; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean ret;

    ret = super();

    if (custTable.mcrMergedParent)
    {
        ret = checkFailed(strFmt("@MCR12337", custTable.AccountNum, custTable.mcrMergedParent));
    }

    return ret;
}

/// <summary>
/// Creates a query string which will load the merged customers
/// in addition to the Ship-to orders for this customer into a string in order to
/// select orders beyond the main customer's orders.
/// </summary>
/// <returns>
/// The created query string with the loaded merged customers
/// along with the the Ship-to orders for this customer.
/// </returns>
/// <remarks>
/// Example of string contents:
/// "((CustAccount == "4000") || (CustAccount == "4001") || (SalesID == "SO-030000"))
/// Safe length for the returnQueryStr = 12843.
/// </remarks>
str additionalCriteria()
{
    CustTable   custTableLocal;
    str         returnQueryStr = '';
    str         customerQueryStr = '';

    // Add the primary and merged customers to the query string.
    customerQueryStr += strFmt('(CustAccount == "%1")', custTable.AccountNum);
    while select AccountNum from custTableLocal
        where custTableLocal.mcrMergedRoot == custTable.AccountNum
    {
        customerQueryStr += ' || ' + strFmt('(CustAccount == "%1")', custTableLocal.AccountNum);
    }

    EventHandlerResult result = new EventHandlerResult();
    element.additionalCriteriaDelegate(customerQueryStr, result);

    if (result.hasResult())
    {
        customerQueryStr = result.result();
    }

    returnQueryStr = '(' + customerQueryStr + ')';

    return returnQueryStr;
}

public int active()
{
    int ret;
    CustInvoiceTrans custInvoiceTrans;

    ret = super();

    if (shouldLimitSalesTableColumns)
    {
        // reads the currently selected record from the DB so that the buffer has all fields populated
        // this way we can be sure that invoking any form actions will work
        this.reread();
        // refreshes the form with the data that was just read from the DB
        this.refresh();
    }

    //Check if return options should be allowed and if not then disable the corresponding buttons.
    if (salesTable.SalesType == SalesType::ReturnItem
        || salesTable.mcrReturnSalesTable().IsExchange
        || salesTable.mcrHasReturn())
    {
        orderReturnInquiry.enabled(true);
    }
    else
    {
        orderReturnInquiry.enabled(false);
    }

    if (salesTable.SalesType != SalesType::ReturnItem)
    {
        select firstonly RecId from custInvoiceTrans
            where custInvoiceTrans.SalesId == salesTable.SalesId;
    }

    if (custInvoiceTrans.RecId)
    {
        soCreateReturn.enabled(true);
    }
    else
    {
        soCreateReturn.enabled(false);
    }

    if (shouldLimitSalesTableColumns)
    {
        MoreOptions.enabled(SalesTable.RecId != 0);
    }

    element.updateRelatedOrderControls();

    return ret;
}

public void init()
{
    super();

    // Cache appropriate display methods.
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayLastShipDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrOrderReleaseDate));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayDeliveryName));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrDisplayTender));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrHasExpeditedLines));
    this.cacheAddMethod(tableMethodStr(SalesTable, mcrGetPaymStatus));
}

/// <summary>
/// Sets the background color for the record.
/// </summary>
/// <param name="_record">
/// The table record for which the background color should be set.
/// </param>
/// <param name="_options">
/// The <c>FormRowDisplayOption</c> object used to set the background color for the record.
/// </param>
public void displayOption(Common _record, FormRowDisplayOption _options)
{
    MCRHoldOrderDisplayColor mcrHoldOrderDisplayColor;

    SalesTable salesTableLocal = _record;

    MCROrderColor mcrReturnOrderDisplayColor, mcrReplacementOrderDisplayColor, mcrSOWithReturnOrderDisplayColor;
    mcrReturnOrderDisplayColor = mcrOrderParameter.MCRReturnOrderColor;
    mcrReplacementOrderDisplayColor = mcrOrderParameter.MCRReplacementOrderColor;
    mcrSOWithReturnOrderDisplayColor = mcrOrderParameter.MCRSOWithReturnOrderColor;

    mcrHoldOrderDisplayColor = SalesParameters::find().mcrHoldOrderDisplayColor;

    if (mcrHoldOrderDisplayColor
        && MCRHoldCodeTrans::findActiveOrderHolds(salesTableLocal.SalesId))
    {
        _options.backColor(mcrHoldOrderDisplayColor);
    }
    else if (mcrReturnOrderDisplayColor && salesTableLocal.SalesType == SalesType::ReturnItem) //Return orders
    {
        _options.backColor(mcrReturnOrderDisplayColor);
    }
    else if (mcrReplacementOrderDisplayColor && (salesTableLocal.SalesType == SalesType::Sales)
            && (salesTableLocal.ReturnItemNum != '')) //Replacement orders
    {
        _options.backColor(mcrReplacementOrderDisplayColor);
    }
    else if (mcrSOWithReturnOrderDisplayColor && salesTableLocal.mcrHasReturn())
    {
        _options.backColor(mcrSOWithReturnOrderDisplayColor);
    }
    else
    {
        _options.clearBackColor();
    }

    super(_record, _options);
}

public void executeQuery()
{
    // Include the sales order for customers merged into this account, need
    // to clear dynalinks for more customer accounts to be included in query.
    if (currentMode == #SalesOrder && custTable.AccountNum)
    {
        QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesTable));

        if (shouldLimitSalesTableColumns && !salesTableColumnsHaveBeenLimited)
        {
            MCRCustomerServiceApplySelectionFields::apply(this.query(), MCRCustomerServiceFormViewMode::SalesOrders);
            salesTableColumnsHaveBeenLimited = true;
        }

        this.query().dataSourceNo(1).clearRanges();
        // Sort the orders by most recent to oldest.
        queryBuildDataSource.addSortField(fieldNum(SalesTable, RecId), SortOrder::Descending);
        // Also add the sales orders to the query where the customer is the ship-to
        // on the orders.  Also moved the criteria to a function.
        str addtlCriteriaStr = this.additionalCriteria();

        if (chosenSalesIdList)
        {
            queryBuildDataSource.addRange(fieldNum(SalesTable, SalesId)).value(strList2str(chosenSalesIdList));
        }

        queryBuildDataSource.clearDynalinks();
        queryBuildDataSource.addRange(fieldNum(SalesTable, CustAccount)).value(addtlCriteriaStr);

        // Only add date filters if date filtering is enabled *AND* either one of the fields is not blank.
        // In case both dates are blank, we don't have to clear any ranges because this is already being done by some logic on this method.
        if (showDateFilters
            && (FromDateFilter.dateValue() != dateNull() || ToDateFilter.dateValue() != dateNull()))
        {
            queryBuildDataSource.addRange(fieldNum(SalesTable, CreatedDateTime))
                                .value(SysQuery::range(FromDateFilter.dateValue(), ToDateFilter.dateValue(), true /* treatOnlyNullAsUnbound */));
        }

        super();
    }
}

