public void selectionChanged()
{
    boolean                 enableButtonPayment, enableButtonInvoice, enableButtonTrackingDetails ;
    CustPackingSlipJour     custPackingSlipJour;

    // Check if the selected order is invoiced or not.
    enableButtonInvoice = !salesTable.ProjId && (salesTable.lastInvoice() !='');

    // Check if payment has been made for the selected order or not.
    if ((salesTable.SalesType != SalesType::ReturnItem) || (salesTable.ReturnStatus != ReturnStatusheader::Created))
    {
        enableButtonPayment = (RetailMCRChannelTable::mcrEnableOrderCompletion(salestable) || RetailCommercePaymentsFeatureHelper::commercePaymentsEnabledForOrder(salestable, funcName()))
                    && salesTable.type().mcrAllowPaymentAccess();
    }

    select firstonly RecId from custPackingSlipJour
        where custPackingSlipJour.SalesId == salesTable.SalesId;

    // Check if packing slip has been generated for the selected order.
    enableButtonTrackingDetails = ((salesTable.lastPackingSlip() != '') && (custPackingSlipJour.RecId));

    // Enabling/disabling the button on the base of flag values.
    MCRCustPaymTableButton.enabled(enableButtonPayment);
    btnInvoice.enabled(enableButtonInvoice);
    btnTrackingDetails.enabled(enableButtonTrackingDetails);

    super();
}

