/// <summary>
/// Creates a query string which will load the merged customers
/// in addition to the Ship-to orders for this customer into a string in order to
/// select orders beyond the main customer's orders.
/// </summary>
/// <returns>
/// The created query string with the loaded merged customers
/// along with the the Ship-to orders for this customer.
/// </returns>
/// <remarks>
/// Example of string contents:
/// "((CustAccount == "4000") || (CustAccount == "4001") || (SalesID == "SO-030000"))
/// Safe length for the returnQueryStr = 12843 (rounded down to 12800 to allow for one
/// more condition to query and closing tags).
/// </remarks>
str additionalCriteria()
{
    CustTable           custTableBuffer;
    MCRHistSalesTable   localMCRHistSalesTable;
    MCRHistSalesLine    localMCRHistSalesLine;
    str                 returnQueryStr = '';
    str                 customerQueryStr = '';
    str                 orderQueryStr = '';

    // Add the primary and merged customers to the query string.
    customerQueryStr += strFmt('(CustAccount == "%1")', custTable.AccountNum);
    while select AccountNum from custTableBuffer
        where custTableBuffer.mcrMergedRoot == custTable.AccountNum
    {
        customerQueryStr += ' || ' + strFmt('(CustAccount == "%1")', custTableBuffer.AccountNum);
    }

    // Add the related orders where this customer is the ship-to to the string.
    while select SalesId from localMCRHistSalesTable
        exists join localMCRHistSalesLine
        where localMCRHistSalesTable.SalesId == localMCRHistSalesLine.SalesId
            && localMCRHistSalesLine.mcrShipToAccountRef == custTable.AccountNum
            && localMCRHistSalesLine.mcrShipToAccountRef   != localMCRHistSalesLine.CustAccount
    {
        if ((strLen(customerQueryStr) + strLen(orderQueryStr)) > 12800)
        {
            break;
        }
        orderQueryStr += strFmt('(SalesId == "%1") || ', localMCRHistSalesTable.SalesId);
    }
    if (orderQueryStr)
    {
        returnQueryStr = '(' + customerQueryStr + ' || ' +  subStr(orderQueryStr, 1, strLen(orderQueryStr)-4) + ')';
    }
    else
    {
        returnQueryStr = '(' + customerQueryStr + ')';
    }
    return returnQueryStr;
}

public void init()
{
    super();
    //Sort lines by line number.
    this.query().dataSourceTable(tableNum(MCRHistSalesLine)).addSortField(fieldNum(MCRHistSalesLine, LineNum));

    // Cache appropriate display methods.
    this.cacheAddMethod(tableMethodStr(MCRHistSalesLine, itemName));
}

public void executeQuery()
{
    //Only load the invoices when showing invoices and customer selected
    if (currentMode == #InvoiceOrder && custTable.AccountNum)
    {
        if (showDateFilters)
        {
            QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(CustInvoiceJour));

            date fromDateValue = FromDateFilter.dateValue();
            date toDateValue = ToDateFilter.dateValue();

            // If the date range was previously added to the data source but both dates are now null, we need to clear the range
            if (invoiceDateQueryRange != null && fromDateValue == dateNull() && toDateValue == dateNull())
            {
                queryBuildDataSource.clearRange(fieldNum(CustInvoiceJour, InvoiceDate));
                invoiceDateQueryRange = null;
            }
            // If the data range was not previously added and one of the dates is not null, add the range to the data source
            else if (!invoiceDateQueryRange && (fromDateValue != dateNull() || toDateValue != dateNull()))
            {
                invoiceDateQueryRange = queryBuildDataSource.addRange(fieldNum(CustInvoiceJour, InvoiceDate));
            }

            // If one of the dates is set, set the *value* the range
            if (fromDateValue != dateNull() || toDateValue != dateNull())
            {
                invoiceDateQueryRange.value(SysQuery::range(FromDateFilter.dateValue(), ToDateFilter.dateValue(), true /* treatOnlyNullAsUnbound */));
            }
        }

        super();
    }
}

